import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.YRY2vHzV.js";import{_ as l,c as i,j as e,a as o,G as s,a4 as n,o as d}from"./chunks/framework.stX8LI7J.js";const k=JSON.parse('{"title":"5G校园专网","description":"","frontmatter":{"author":"HeZi"},"headers":[],"relativePath":"Life/Network/5GCampusPrivateNetwork.md","filePath":"Life/Network/5GCampusPrivateNetwork.md","lastUpdated":1725024892000}'),c={name:"Life/Network/5GCampusPrivateNetwork.md"};function h(p,a,m,u,f,b){const t=r;return d(),i("div",null,[a[0]||(a[0]=e("h1",{id:"_5g校园专网",tabindex:"-1"},[o("5G校园专网 "),e("a",{class:"header-anchor",href:"#_5g校园专网","aria-label":'Permalink to "5G校园专网"'},"​")],-1)),s(t,{readTime:"2",words:"792"}),a[1]||(a[1]=n(`<h2 id="什么是5g校园专网" tabindex="-1">什么是5G校园专网 <a class="header-anchor" href="#什么是5g校园专网" aria-label="Permalink to &quot;什么是5G校园专网&quot;">​</a></h2><p><strong>5G校园专网</strong>简单来说，就是把5G网络当作校园网络的扩展和补充，校园网络的接入访问覆盖范围扩展到全国。在不使用<a href="https://live.cumt.asia/Life/Network/VPN.html" target="_blank" rel="noreferrer">VPN</a>的情况下就可以访问校园内网。</p><h2 id="收费详情" tabindex="-1">收费详情 <a class="header-anchor" href="#收费详情" aria-label="Permalink to &quot;收费详情&quot;">​</a></h2><p>师生申请开通电信5G校园专网业务后，可免费使用至2024年12月31日。2025年1月1日起，每月收取1元功能费。访问5G校园专网产生的流量使用所开通号码本身套餐内的通用流量。</p><h2 id="师生如何开通-关闭5g校园专网业务" tabindex="-1">师生如何开通/关闭5G校园专网业务？ <a class="header-anchor" href="#师生如何开通-关闭5g校园专网业务" aria-label="Permalink to &quot;师生如何开通/关闭5G校园专网业务？&quot;">​</a></h2><p>使用5G终端，且号码归属地为<strong>徐州电信</strong>(可以不为校园卡)的师生可实现不换卡、不换号直接申请开通5G校园专网。</p><h3 id="e矿大2-0-钉钉-——-工作台" tabindex="-1">e矿大2.0/钉钉 —— 工作台 <a class="header-anchor" href="#e矿大2-0-钉钉-——-工作台" aria-label="Permalink to &quot;e矿大2.0/钉钉 —— 工作台&quot;">​</a></h3><p>打开e矿大2.0-工作台，点击【5G专网】进入5G专网自助服务平台-【5G专网申请】-【5G专网申请】。</p><img src="https://s2.loli.net/2024/08/28/SFDoMW4pxb1PCyR.png" alt="35ac3c9c7d6f6e3945b5be7915bd87cd.png" class="mx-auto w-70%"><ul><li>若工作台首页未展示【5G专网】入口，请点击【更多】至应用中心内查找使用</li></ul><img src="https://s2.loli.net/2024/08/28/2vkSNTEUqAJg9pZ.png" alt="506fd68ed2a590dbd763ba475ec96e84.png" class="mx-auto w-70%"><h3 id="填写申请" tabindex="-1">填写申请 <a class="header-anchor" href="#填写申请" aria-label="Permalink to &quot;填写申请&quot;">​</a></h3><p>填写个人信息后，阅读并同意跳出的条款内容。自动返回上一页后，点击【提交】，在签名框中手写签名，点击【保存签名】。</p><img src="https://s2.loli.net/2024/08/28/uEiWq5R7NBVt6De.png" alt="854023704cc1ad267bb5316dad853c6d.png" class="mx-auto w-70%"><img src="https://s2.loli.net/2024/08/28/SBiqsCAygTl8Dd6.png" alt="d79bcaa347e0faa5f8f5a3c29abc7bf9.png" class="mx-auto w-70%"><img src="https://s2.loli.net/2024/08/28/Q6NIgScaBojR5zr.webp" alt="640.webp" class="mx-auto w-70%"><h3 id="等待短信-飞行模式" tabindex="-1">等待短信-飞行模式 <a class="header-anchor" href="#等待短信-飞行模式" aria-label="Permalink to &quot;等待短信-飞行模式&quot;">​</a></h3><p>您提交的申请开通成功后，会收到运营商发送的短信。收到短信后，请打开手机的飞行模式，等待约30秒，再关闭飞行模式，即可体验5G校园专网业务。</p><p><img src="https://s2.loli.net/2024/08/28/rERIc4feOnMZsvi.png" alt="c52791e21fc56135a4f4914666dde5e2_720.png" title="中国电信开通成功短信提醒
"></p><h2 id="如何关闭5g校园专网业务" tabindex="-1">如何关闭5G校园专网业务 <a class="header-anchor" href="#如何关闭5g校园专网业务" aria-label="Permalink to &quot;如何关闭5G校园专网业务&quot;">​</a></h2><p>访问5G校园专网自助申请页面，点击【业务更改】进入解绑页面，选择手机号对应运营商，填写手机号，输入验证码，点击【解绑】即可.</p><h2 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h2><ul><li>开通5G校园专网业务以申请时提交的手机号为准，与统一身份认证中绑定的手机号无关。</li><li>您仅能为本人手机号开通专网业务。提交申请信息后，运营商将验证手机号对应的机主与申请人是否一致，如不一致则无法开通。</li><li>若您持有同一家运营商的多个手机号，您仅能为其中一个手机号开通5G校园专网业务。</li><li>持有非徐州号卡的师生，如需开通5G校园专网，可自行前往校内中国电信营业厅，办理徐州号卡，再申请开通。</li></ul><h2 id="管理部门" tabindex="-1">管理部门 <a class="header-anchor" href="#管理部门" aria-label="Permalink to &quot;管理部门&quot;">​</a></h2><ul><li><p>中国电信南湖校区营业厅咨询电话：0516-83599002。</p></li><li><p>如果在开通5G校园专网过程中遇到问题，可拨打电话83592179或4000089233咨询。</p></li></ul>`,25))])}const q=l(c,[["render",h]]);export{k as __pageData,q as default};
