import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.B_ghoC2Z.js";import{_ as i,c as l,j as e,a as o,G as n,a4 as s,o as h}from"./chunks/framework.C83m97Xz.js";const x=JSON.parse('{"title":"5G校园专网","description":"","frontmatter":{"author":"HeZi"},"headers":[],"relativePath":"Life/Network/5GCampusPrivateNetwork.md","filePath":"Life/Network/5GCampusPrivateNetwork.md","lastUpdated":1731592126000}'),d={name:"Life/Network/5GCampusPrivateNetwork.md"};function p(c,t,g,m,u,G){const a=r;return h(),l("div",null,[t[0]||(t[0]=e("h1",{id:"_5g校园专网",tabindex:"-1"},[o("5G校园专网 "),e("a",{class:"header-anchor",href:"#_5g校园专网","aria-label":'Permalink to "5G校园专网"'},"​")],-1)),n(a,{readTime:"4",words:"1.2k"}),t[1]||(t[1]=s(`<h2 id="什么是5g校园专网" tabindex="-1">什么是5G校园专网 <a class="header-anchor" href="#什么是5g校园专网" aria-label="Permalink to &quot;什么是5G校园专网&quot;">​</a></h2><p><strong>5G校园专网</strong>是指通过运营商5G通信网络切片、DNN、UPF用户平面流量牵引、UCLC分流等技术，实现运营商网络与校园网互联互通的校园专用网络。通过将5G网络和校园网络进行融合，把5G网络当作校园网络的扩展和补充，校园网络的接入访问覆盖范围扩展到全国。</p><p><strong>简单来说</strong>，就是把5G网络当作校园网络的扩展和补充，校园网络的接入访问覆盖范围扩展到全国。在不使用<a href="https://live.cumt.asia/Life/Network/VPN.html" target="_blank" rel="noreferrer">VPN</a>的情况下就可以访问校园内网。</p><p>学校联合中国电信、中国移动和中国联通完成5G校园专网的建设与测试，于本学期初上线试运行。经优化、完善与评估，目前已具备全面开通的能力与条件。即日起 <strong>中国矿业大学5G校园专网全面开通</strong> ，师生可申请开通相关业务。</p><h2 id="收费详情" tabindex="-1">收费详情 <a class="header-anchor" href="#收费详情" aria-label="Permalink to &quot;收费详情&quot;">​</a></h2><p>师生申请开通5G校园专网业务后，可免费使用至2025年5月31日。2025年6月1日起，运营商每月收取1元功能费。访问5G校园专网产生的流量使用所开通号码本身套餐内的通用流量。</p><table tabindex="0"><thead><tr><th>运营商</th><th>移动</th><th>联通</th><th>电信</th></tr></thead><tbody><tr><td>支持终端</td><td>仅5G终端</td><td>4G、5G终端</td><td>4G、5G终端</td></tr></tbody></table><h2 id="师生如何开通-关闭5g校园专网业务" tabindex="-1">师生如何开通/关闭5G校园专网业务？ <a class="header-anchor" href="#师生如何开通-关闭5g校园专网业务" aria-label="Permalink to &quot;师生如何开通/关闭5G校园专网业务？&quot;">​</a></h2><p>号码归属地为徐州移动/联通/电信的师生可实现 <strong>不换卡、不换号直接申请开通5G校园专网业务</strong> 。以下以徐州电信为例，介绍开通5G校园专网业务的操作流程。</p><h3 id="e矿大2-0-钉钉-——-工作台" tabindex="-1">e矿大2.0/钉钉 —— 工作台 <a class="header-anchor" href="#e矿大2-0-钉钉-——-工作台" aria-label="Permalink to &quot;e矿大2.0/钉钉 —— 工作台&quot;">​</a></h3><p>打开e矿大2.0-工作台，点击【5G专网】进入5G专网自助服务平台-【5G专网申请】-【5G专网申请】。</p><ul><li>若工作台首页未展示【5G专网】入口，请点击【更多】至应用中心内查找使用</li></ul><div style="display:flex;justify-content:center;align-items:center;gap:0;"><img src="https://s2.loli.net/2024/08/28/SFDoMW4pxb1PCyR.png" alt="Image 1" style="width:50%;margin:0;"><img src="https://s2.loli.net/2024/08/28/2vkSNTEUqAJg9pZ.png" alt="Image 2" style="width:50%;margin:0;"></div><h3 id="填写申请" tabindex="-1">填写申请 <a class="header-anchor" href="#填写申请" aria-label="Permalink to &quot;填写申请&quot;">​</a></h3><p>填写个人信息后，阅读并同意跳出的条款内容。自动返回上一页后，点击【提交】，在签名框中手写签名，点击【保存签名】。</p><div style="display:flex;justify-content:center;align-items:center;gap:0;"><img src="https://s2.loli.net/2024/08/28/uEiWq5R7NBVt6De.png" alt="Image 1" style="flex:1 1 auto;height:auto;max-height:400px;object-fit:cover;margin:0;"><img src="https://s2.loli.net/2024/08/28/SBiqsCAygTl8Dd6.png" alt="Image 2" style="flex:1 1 auto;height:auto;max-height:400px;object-fit:cover;margin:0;"><img src="https://s2.loli.net/2024/08/28/Q6NIgScaBojR5zr.webp" alt="Image 3" style="flex:1 1 auto;height:auto;max-height:400px;object-fit:cover;margin:0;"></div><h3 id="等待短信-飞行模式" tabindex="-1">等待短信-飞行模式 <a class="header-anchor" href="#等待短信-飞行模式" aria-label="Permalink to &quot;等待短信-飞行模式&quot;">​</a></h3><p>您提交的申请开通成功后，会收到运营商发送的短信。收到短信后，请打开手机的飞行模式，等待约30秒，再关闭飞行模式，即可体验5G校园专网业务。</p><p><img src="https://s2.loli.net/2024/08/28/rERIc4feOnMZsvi.png" alt="c52791e21fc56135a4f4914666dde5e2_720.png" title="中国电信开通成功短信提醒
"></p><h2 id="如何关闭5g校园专网业务" tabindex="-1">如何关闭5G校园专网业务 <a class="header-anchor" href="#如何关闭5g校园专网业务" aria-label="Permalink to &quot;如何关闭5G校园专网业务&quot;">​</a></h2><p>访问5G校园专网自助申请页面，点击【业务更改】进入解绑页面，选择手机号对应运营商，填写手机号，输入验证码，点击【解绑】即可.</p><h2 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h2><ul><li>开通5G校园专网业务以申请时提交的手机号为准，与统一身份认证中绑定的手机号无关。</li><li>您仅能为本人手机号开通专网业务。提交申请信息后，运营商将验证手机号对应的机主与申请人是否一致，如不一致则无法开通。</li><li>若您持有同一家运营商的多个手机号，您仅能为其中一个手机号开通5G校园专网业务。</li><li>持有非徐州号卡的师生，如需开通5G校园专网，可自行前往校内中国电信营业厅，办理徐州号卡，再申请开通。</li><li>双卡用户使用5G校园专网业务，请确保您的设备使用的是已开通业务卡号的数据流量。</li><li>师生毕业/离校后将无法使用5G校园专网业务，为避免继续产生不必要的费用，请您自行于毕业/离校当月前往校内运营商营业厅，关闭5G校园专网业务。</li></ul><h2 id="管理部门" tabindex="-1">管理部门 <a class="header-anchor" href="#管理部门" aria-label="Permalink to &quot;管理部门&quot;">​</a></h2><ul><li>中国电信南湖校区营业厅咨询电话：0516-83599002。</li><li>如果在开通5G校园专网过程中遇到问题，可拨打电话83592179或4000089233咨询。</li></ul><h2 id="参考链接" tabindex="-1">参考链接 <a class="header-anchor" href="#参考链接" aria-label="Permalink to &quot;参考链接&quot;">​</a></h2><ul><li><a href="https://mp.weixin.qq.com/s/hNeoAYrtWiATRGq6ytPbYA" target="_blank" rel="noreferrer"> 中国矿业大学5G校园专网全面开通，快来申请吧!</a></li></ul>`,27))])}const q=i(d,[["render",p]]);export{x as __pageData,q as default};
