import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.DQViIFNj.js";import{_ as o,c as n,j as e,a as s,G as l,a4 as i,o as h}from"./chunks/framework.stX8LI7J.js";const k=JSON.parse('{"title":"统一身份认证","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"Life/Network/SSO.md","filePath":"Life/Network/SSO.md","lastUpdated":1724847395000}'),c={name:"Life/Network/SSO.md"};function d(p,a,u,m,f,g){const t=r;return h(),n("div",null,[a[0]||(a[0]=e("h1",{id:"统一身份认证",tabindex:"-1"},[s("统一身份认证 "),e("a",{class:"header-anchor",href:"#统一身份认证","aria-label":'Permalink to "统一身份认证"'},"​")],-1)),l(t,{readTime:"1",words:"543"}),a[1]||(a[1]=i('<h2 id="什么是统一身份认证" tabindex="-1">什么是统一身份认证 <a class="header-anchor" href="#什么是统一身份认证" aria-label="Permalink to &quot;什么是统一身份认证&quot;">​</a></h2><p>统一身份认证是一套方便师生用户进行身份的认证服务，同时提供用户的个人账号信息管理服务。其可以在<strong>融合门户</strong>中实现单点登录，即用户在一个浏览器的会话期中只需登录一次就能进入所有其拥有访问权限的其他应用系统。</p><p>登陆界面如图：</p><img src="https://s2.loli.net/2024/08/22/iT9nMs1b2YODBgy.png" alt="image-20240822215824256" class="mx-auto w-70%"><p>在未登录状态下访问一些应用的时候会跳转到此界面。</p><h2 id="需要统一身份认证的地方" tabindex="-1">需要统一身份认证的地方 <a class="header-anchor" href="#需要统一身份认证的地方" aria-label="Permalink to &quot;需要统一身份认证的地方&quot;">​</a></h2><p>设置好统一身份认证账号密码后，几乎能够登录校内所有与个人有关的系统。如：教务系统、财务系统、一体化网络登录、一卡通系统、公寓系统、师生服务热线等，<strong>矿小助APP</strong>、<strong>CUMT微生活</strong>等登录用的也是这套账号密码，所以请尽可能保护好自己的统一身份认证账号密码。</p><h2 id="登陆方式" tabindex="-1">登陆方式 <a class="header-anchor" href="#登陆方式" aria-label="Permalink to &quot;登陆方式&quot;">​</a></h2><p>首次登陆前须先进行<strong>激活</strong>（见下文），无默认密码。</p><ul><li>账号密码登录：账号为学工号或你设置的别名，密码为你设置的密码</li><li>验证码登录：账号为你的学工号，并点击“获取验证码”，输入发送到绑定手机上的验证码</li><li>微信扫码登录（需先绑定微信）</li></ul><h2 id="认证平台个人服务中心" tabindex="-1">认证平台个人服务中心 <a class="header-anchor" href="#认证平台个人服务中心" aria-label="Permalink to &quot;认证平台个人服务中心&quot;">​</a></h2><p>链接：<a href="http://authserver.cumt.edu.cn/personalInfo/" target="_blank" rel="noreferrer">http://authserver.cumt.edu.cn/personalInfo/</a> 使用统一身份认证账号、密码登录</p><img src="https://s2.loli.net/2024/08/22/vnX76MuObcCPwmI.png" alt="image-20240822214959660" class="mx-auto w-70%"><p>在平台中，我们可以进行登陆别名、登陆密码、绑定手机、绑定邮箱、关联第三方账号的设置与修改，在认证记录中，可以看到当前账号的登陆记录。</p><h2 id="统一身份认证账号激活" tabindex="-1">统一身份认证账号激活 <a class="header-anchor" href="#统一身份认证账号激活" aria-label="Permalink to &quot;统一身份认证账号激活&quot;">​</a></h2><p>首次登陆前须先进行激活，点击<a href="https://authserver.cumt.edu.cn/authserver/login" target="_blank" rel="noreferrer">统一身份认证平台 (cumt.edu.cn)</a>，点击“账号激活”，按照提示输入学工号、证件号、绑定手机号、设置密码即可。</p>',16))])}const x=o(c,[["render",d]]);export{k as __pageData,x as default};
