import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.YRY2vHzV.js";import{_ as o,c as s,j as e,a as i,G as l,a4 as n,o as p}from"./chunks/framework.stX8LI7J.js";const g=JSON.parse('{"title":"校际交通","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"Life/Traffic/BetweenCampus.md","filePath":"Life/Traffic/BetweenCampus.md","lastUpdated":1727713674000}'),h={name:"Life/Traffic/BetweenCampus.md"};function d(c,a,m,u,f,b){const t=r;return p(),s("div",null,[a[0]||(a[0]=e("h1",{id:"校际交通",tabindex:"-1"},[i("校际交通 "),e("a",{class:"header-anchor",href:"#校际交通","aria-label":'Permalink to "校际交通"'},"​")],-1)),l(t,{readTime:"1",words:"371"}),a[1]||(a[1]=n('<p>校际交通，指往返于南湖、文昌两校区之间的交通方式。主要包括：校车、公交、打车、自驾。</p><h2 id="校车" tabindex="-1">校车 <a class="header-anchor" href="#校车" aria-label="Permalink to &quot;校车&quot;">​</a></h2><h3 id="运行路线图、停靠站点" tabindex="-1">运行路线图、停靠站点 <a class="header-anchor" href="#运行路线图、停靠站点" aria-label="Permalink to &quot;运行路线图、停靠站点&quot;">​</a></h3><p>如图所示，在每个站点附近均可以看到校车的候车亭，请排队上车。</p><p>南线北线的区别在于文昌去往南湖的校车，在驶过行健楼站时的行驶方向：</p><ul><li><p><strong>北线</strong>是向北门方向行驶，经过几个学院楼和图书馆</p></li><li><p><strong>南线</strong>是向南门方向行驶，路过公教楼区，最后均到达二食堂</p></li></ul><p>南线班车的开行，是方便文昌校区住宿同学来南湖校区上课的。</p><img src="https://s2.loli.net/2024/08/30/ghULBIRfHZS19cl.png" alt="w-100" class="mx-auto w-100%"><img src="https://s2.loli.net/2024/08/30/sTaqx9tdSUD85ui.png" alt="w-100" class="mx-auto w-100%"><h3 id="运行时刻表-2024-8-31-2025-1-19" tabindex="-1">运行时刻表（2024.8.31-2025.1.19） <a class="header-anchor" href="#运行时刻表-2024-8-31-2025-1-19" aria-label="Permalink to &quot;运行时刻表（2024.8.31-2025.1.19）&quot;">​</a></h3><p>节假日指平时周末的双休以及国家法定节假日。若周六日调休则执行工作日班车时刻表。</p><img src="https://s2.loli.net/2024/08/30/XBJ95pEgambuz4Q.webp" alt="图片" class="mx-auto w-70%"><p>其中，18:15-21:00间，班车循环发车，坐满发车，约15-40分钟一班。</p><p>最新时刻请关注“CUMT微生活”微信公众号。</p><h3 id="校车费用" tabindex="-1">校车费用 <a class="header-anchor" href="#校车费用" aria-label="Permalink to &quot;校车费用&quot;">​</a></h3><p>1.5元/次，支持刷校园卡或扫码支付。</p><h3 id="运行时间" tabindex="-1">运行时间 <a class="header-anchor" href="#运行时间" aria-label="Permalink to &quot;运行时间&quot;">​</a></h3><p>单趟约20分钟左右。</p><h3 id="反馈-咨询-投诉" tabindex="-1">反馈/咨询/投诉 <a class="header-anchor" href="#反馈-咨询-投诉" aria-label="Permalink to &quot;反馈/咨询/投诉&quot;">​</a></h3><p>0516-83885871</p><p>负责人电话：13685109507。</p><h2 id="公交" tabindex="-1">公交 <a class="header-anchor" href="#公交" aria-label="Permalink to &quot;公交&quot;">​</a></h2><p>东门乘坐51路至“大学城”下车。</p>',23))])}const _=o(h,[["render",d]]);export{g as __pageData,_ as default};
