import{_ as s}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CEM7htoN.js";import{_ as a,c as l,j as o,a as i,G as n,a4 as e,o as g}from"./chunks/framework.C83m97Xz.js";const k=JSON.parse('{"title":"来校/离校指南","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"Life/Traffic/Guide.md","filePath":"Life/Traffic/Guide.md","lastUpdated":1732433670000}'),p={name:"Life/Traffic/Guide.md"};function c(d,t,h,u,m,f){const r=s;return g(),l("div",null,[t[0]||(t[0]=o("h1",{id:"来校-离校指南",tabindex:"-1"},[i("来校/离校指南 "),o("a",{class:"header-anchor",href:"#来校-离校指南","aria-label":'Permalink to "来校/离校指南"'},"​")],-1)),n(r,{readTime:"3",words:"1.1k"}),t[1]||(t[1]=e('<h2 id="公交进校园-活动" tabindex="-1">“公交进校园”活动 <a class="header-anchor" href="#公交进校园-活动" aria-label="Permalink to &quot;“公交进校园”活动&quot;">​</a></h2><p>在每年寒暑假放假/返校/毕业生离校，收假/迎新的时候，学校会与公交协商，开展“公交进校园”活动。</p><p>学校乘车地点为二食堂广场东侧，南湖校区至徐州站，徐州东站，观音机场的票价分别为5元/8元/30元。</p><p><strong>公交进校园的优点在哪里？</strong></p><ul><li>免于提着行李赶赴学校大门的麻烦</li><li>路上不停车，一站直达</li></ul><p>前往徐州站/徐州东站的车每次均有时间安排表，在发车时间前坐满也会发车，最好不要踩点去。</p><p>前往观音机场的车须在“<strong>徐州空港巴士公众号</strong>”上购票，按点乘车。</p><p>每年的具体安排请关注“<strong>CUMT微生活</strong>”公众号，有最新的消息将会及时收到推送。</p><h2 id="南湖校区→徐州站" tabindex="-1">南湖校区→徐州站 <a class="header-anchor" href="#南湖校区→徐州站" aria-label="Permalink to &quot;南湖校区→徐州站&quot;">​</a></h2><p>给出几种常用方案：</p><ol><li><strong>时间充足型</strong>。学校东门斜对面“<strong>矿大南湖校区东门</strong>”公交站，乘坐<strong>51路</strong>即可，一个小时多的样子。</li><li><strong>略赶时间型</strong>。与<strong>51路</strong>同站点，乘坐<strong>63/47</strong>路，至“<strong>中医院</strong>”站换乘<strong>11路</strong>即可，全程30-50分。</li><li><strong>打车</strong>，15-25分钟，单人15-20元，建议去东门对面打车，相对便宜一点。</li><li><strong>公交进校园活动</strong>（仅放假/返校时有，见下面介绍）。</li></ol><h2 id="徐州站→南湖校区" tabindex="-1">徐州站→南湖校区 <a class="header-anchor" href="#徐州站→南湖校区" aria-label="Permalink to &quot;徐州站→南湖校区&quot;">​</a></h2><ol><li><p><strong>时间充足型</strong>。徐州站出站口出来以后向北拐，一直往前走，在路左面会看到一个停车场，51路始发站就在这个停车场里面。</p><img src="https://s2.loli.net/2023/07/11/xhM6wFJqPAdrDeH.png" alt="w-50" class="mx-auto w-50%"></li><li><p><strong>略赶时间型</strong>。首先乘坐<strong>11路</strong>，徐州站出站口出来向南看到十字路口的时候向西，可以看到中国邮政储蓄银行，<strong>11路</strong>的站牌就在这里（在路南，不要走错了）。乘坐<strong>11路</strong>至中医院，即可同站换乘<strong>63路</strong>，抵达南湖校区东门。</p></li></ol><img src="https://s2.loli.net/2023/07/11/r2UgJ4zoTXMFSpa.png" alt="w-50" class="mx-auto w-50%"><ol start="3"><li><strong>打车</strong>，20-30分钟，单人15-20元，建议去出站口的位置叫滴滴。</li><li><strong>公交进校园活动</strong>。</li></ol><h2 id="南湖校区→徐州东站" tabindex="-1">南湖校区→徐州东站 <a class="header-anchor" href="#南湖校区→徐州东站" aria-label="Permalink to &quot;南湖校区→徐州东站&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">乘车提示</p><p>凡购票信息为4A4B、5A5B、7A7B检票的旅客，请从地铁通道到东广场进站候车。徐州东站全部列车都可以在东站房检票，车次尾数为双的列车可以去西广场进站。两广场间需要走不短的一段路，请务必注意。</p></div><ul><li>东门乘坐<strong>63路</strong>公交至<strong>彭城饭店</strong>站，向北走至苏宁附近，即可看到地铁1号线的入口，乘坐地铁1号线至徐州东站即可，全程1小时左右。</li><li>东门乘坐<strong>47路</strong>公交至<strong>中山南路</strong>站，向北走至淮海东路十字路口，过马路后向东走，即可看到地铁1号线的入口，乘坐地铁1号线即可，全程1小时+左右。</li></ul><img src="https://s2.loli.net/2023/07/12/l9GmaRNSAeQZBH8.png" alt="image-20230712233824474" class="mx-auto w-70%"><ul><li><p>乘坐<strong>校车</strong>至文昌校区，文昌校区北门下车，出文昌北门后过马路向西，即可看到地铁3号线的入口，乘坐3号线（下淀方向）至徐州火车站站，换乘地铁1号线至徐州东站。</p><img src="https://s2.loli.net/2023/07/12/WBjCQeNP5wFJkfg.png" alt="image-20230712233824474" class="mx-auto w-70%"></li><li><p><strong>打车</strong>，30-40分钟，单人60元左右，建议去东门对面打车，相对便宜一点。</p></li><li><p><strong>公交进校园活动</strong>（仅放假/返校时有，见下面介绍）。</p></li></ul><h2 id="徐州东站→南湖校区" tabindex="-1">徐州东站→南湖校区 <a class="header-anchor" href="#徐州东站→南湖校区" aria-label="Permalink to &quot;徐州东站→南湖校区&quot;">​</a></h2><ul><li>地铁1号线至彭城广场站，转47路至南湖校区东门。</li><li>10路至彭城广场转63路至南湖校区东门。</li><li>乘坐地铁至文昌校区，转校车。</li></ul><h2 id="南湖校区→观音国际机场" tabindex="-1">南湖校区→观音国际机场 <a class="header-anchor" href="#南湖校区→观音国际机场" aria-label="Permalink to &quot;南湖校区→观音国际机场&quot;">​</a></h2><ul><li>徐州汽车总站乘坐机场大巴，提前在巴士管家购票，25元。</li><li>先去徐州东站，购买到观音机场高铁站的票，之后乘坐免费的摆渡车抵达机场。</li><li><strong>打车</strong>，130元左右。</li><li><strong>公交进校园活动</strong>。</li><li>定制机场巴士，可在东门或南门上车，票价40，联系17327676735。</li></ul><p>有关机场巴士的最新消息，请关注“徐州观音国际机场”公众号。</p>',25))])}const q=a(p,[["render",c]]);export{k as __pageData,q as default};
