import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CSBJqdyJ.js";import{_ as s,c as p,I as o,j as a,a as i,a6 as l,o as r}from"./chunks/framework.DrBMTUlI.js";const P=JSON.parse('{"title":"维卡食舍","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/GuLou/维卡食舍.md","filePath":"Play/Eat/OutSchool/GuLou/维卡食舍.md","lastUpdated":1721287259000}'),c={name:"Play/Eat/OutSchool/GuLou/维卡食舍.md"},n=a("h1",{id:"维卡食舍",tabindex:"-1"},[i("维卡食舍 "),a("a",{class:"header-anchor",href:"#维卡食舍","aria-label":'Permalink to "维卡食舍"'},"​")],-1),m=l('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>九龙湖西路 1-3 号（九龙湖福地阁西门对面,近九龙湖地铁站 1 号口）</p><p><img src="https://s2.loli.net/2024/07/18/X6waV8mtdSPqsuN.png" alt="image-20240718001335029" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了清新杂莓奶昔、自助冷饮无限续杯、全新什锦小食汇、“佩佩罗妮”烟肉披萨、火烤脆鸡沙拉。</p><p>这家店给人的感觉很像走在意大利的街头，突然闻到了 pizza 的味道，你顺着香味推门走进店里，发现里面有的不仅只是香气四溢的 pizza，还有一位看起来就很懂得生活的店主。</p><p>店并不算很大，一层有三分之一的地方都是厨房，但是整个店的风格像是店主对于“温暖幸福”这四个字的具象化诠释，有手绘的玻璃屏风、冬日真的会扔木头进去生火的壁炉、随处可见的生活化的装饰···上二层的楼梯底下摆了瓷器和扎染的手工作品以及一些店主周游和外出研学的照片。</p><p>先上桌的是奶昔和冷饮，奶昔做的口感很绵密，像是融化了的冰淇淋；冷饮跟宜家自助续杯的饮料味道如初一辙，是“没有选择的选择”，说的再直白一点，除非你特别爱喝这个，不然你可以选点别的。炸物分量很足，选恐福音，不用纠结到底选哪个因为它直接跟你说“你不用做选择，我全都有”，从炸鸡块到炸豆角，一盘炸物里面至少有六种食材，不管荤素，裹着酱送进嘴里你会明白为什么 Rick 这么执着于麦当劳的四川辣酱。</p><p>烤鸡沙拉是一个不出错的选择，可以作为配餐存在但永远不会成为餐桌上的主菜；pizza 就是那道华丽的主菜，等的时间很久，但在你知道饼底需要进炉子烤两遍，并且看到 pizza 上面布满的芝士和烟肉的时候你会理解等待是值得的。烤了两遍的饼底带来的是特有的烟熏焦香的风味，也锁住了 pizza 上面食材的水分，私以为对于食物好吃的最高评价是“没错，就该是这个味”，这个 pizza 做到了。</p><p>我会为了这家店给我带来的温暖感觉再去的，当然也是为了这口不是芝士饼底但丝毫不减好吃的 pizza。</p><p>店里面还有单独售卖下午茶的蛋糕和迷你装的酒，付款之后可以联系服务人员帮你拿。出餐比较慢，但是菜品分量很够，而且好吃。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>饭点建议提前打电话预约位置，出餐慢但值得等待。</p><p><img src="https://s2.loli.net/2024/07/18/2rxlcXdQiM5mH7S.png" alt="image-20240718001415713" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/cn5bEPUI8F7ajG9.png" alt="image-20240718001419347" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/TIH52aXBb1G684d.png" alt="image-20240718001423279" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/OxXJr5QWcSKGyZN.png" alt="image-20240718001427622" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/iBaeHL1ct3MEz6x.png" alt="image-20240718001441153" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/9B1ANdnxpCgaKRH.png" alt="image-20240718001446026" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/VacrktMX51Oix3l.png" alt="image-20240718001449777" class="mx-auto w-80%"><br></p>',20);function d(h,_,u,g,x,b){const t=e;return r(),p("div",null,[n,o(t,{readTime:"2",words:"844"}),m])}const S=s(c,[["render",d]]);export{P as __pageData,S as default};
