import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.yBKqk1BA.js";import{_ as o,c as s,I as r,j as a,a as l,a6 as n,o as i}from"./chunks/framework.37nz5jb7.js";const S=JSON.parse('{"title":"一家人人生一串","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/一家人人生一串.md","filePath":"Play/Eat/OutSchool/QuanShan/一家人人生一串.md","lastUpdated":1721233701000}'),c={name:"Play/Eat/OutSchool/QuanShan/一家人人生一串.md"},p=a("h1",{id:"一家人人生一串",tabindex:"-1"},[l("一家人人生一串 "),a("a",{class:"header-anchor",href:"#一家人人生一串","aria-label":'Permalink to "一家人人生一串"'},"​")],-1),h=n('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>高德地图上可以搜得到，打车也可以定位到，在苏堤路上。注意，大众点评上面是搜不到这家的。</p><p><img src="https://s2.loli.net/2024/07/18/ndpaLUjfIRKmtzh.png" alt="image-20240718002520960" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>过纪录片《人生一串》的徐州烧烤店（第一季第二集），这含金量自是不必说的。店面里20来张桌子的样子，烧烤可以随意点，都好吃，蘸料和调味不会抢了食材本身的味道，而且可以吃到食材挺新鲜的，最难得的是不会觉得油腻，上菜速度也挺快。烧烤是烤好了的，桌上有烤炉可以放上去热着继续吃（小心烫着）。</p><p>店里的饮料选择不多，但是隔壁有家便利店可以去便利店里面买自己喜欢的。小吃就是普通的冰粉和藕粉之类的。</p><h2 id="环境" tabindex="-1">🌏环境 <a class="header-anchor" href="#环境" aria-label="Permalink to &quot;:earth_asia:环境&quot;">​</a></h2><p>饭点人可能比较多，建议提前打电话预留座位。</p><h2 id="价格" tabindex="-1">💰价格 <a class="header-anchor" href="#价格" aria-label="Permalink to &quot;:moneybag:价格&quot;">​</a></h2><p>价格感觉很亲民了，就是烧烤的普遍价格。</p><p><img src="https://s2.loli.net/2024/07/18/xYqwArECyBP4Fd9.png" alt="image-20240718002550872" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/E5dNPrRTiHJbaV1.png" alt="image-20240718002603301" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/tRzCgDZSQsPq95Y.png" alt="image-20240718002610850" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/nGJHiMf56VkWd4R.png" alt="image-20240718002620195" class="mx-auto w-80%"><br></p>',14);function d(_,m,u,g,f,b){const t=e;return i(),s("div",null,[p,r(t,{readTime:"1",words:"350"}),h])}const q=o(c,[["render",d]]);export{S as __pageData,q as default};
