import{_ as o}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.DzE2HbnG.js";import{_ as r,o as s,c as n,j as t,a as i,G as l,a4 as p}from"./chunks/framework.Bd6xQ1Yj.js";const x=JSON.parse('{"title":"三顺甜品店","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/三顺甜品店.md","filePath":"Play/Eat/OutSchool/QuanShan/三顺甜品店.md","lastUpdated":1721233701000}'),h={name:"Play/Eat/OutSchool/QuanShan/三顺甜品店.md"};function d(c,a,m,u,_,f){const e=o;return s(),n("div",null,[a[0]||(a[0]=t("h1",{id:"三顺甜品店",tabindex:"-1"},[i("三顺甜品店 "),t("a",{class:"header-anchor",href:"#三顺甜品店","aria-label":'Permalink to "三顺甜品店"'},"​")],-1)),l(e,{readTime:"1",words:"430"}),a[1]||(a[1]=p('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>湖北路弘阳广场商业楼208</p><p><img src="https://s2.loli.net/2024/07/18/nOjTUue6taCBr3x.png" alt="image-20240718000645079" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了招牌的凤梨生椰斑斓蛋糕和抹茶草莓牛乳。</p><p>【凤梨生椰斑斓蛋糕】感觉放的稍微有点久，外层的海绵蛋糕已经微微变色的，但不影响吃。奶油的调味蛮不错的，椰香很浓郁。它们家的凤梨听说都是亲自熬的，还不错，保留了凤梨多汁的口感同时也没有太过甜。中间海绵蛋糕的口感稍微有一点点突出，如果做的更绵密一些会感觉更完美。整体很清新的味道，奶油不厚重，生椰奶油衬托出了凤梨的清爽，也给了蛋糕浓郁的回味。</p><p>【抹茶草莓牛乳】没有什么抹茶味，整体无功无过，只能说拍照挺好看。</p><h2 id="环境" tabindex="-1">🌏环境 <a class="header-anchor" href="#环境" aria-label="Permalink to &quot;:earth_asia:环境&quot;">​</a></h2><p>跟花店开在了一起，店里的布置也像个奶油蛋糕，下午去的时候店里没有开灯，室内感觉会有些许暗。环境氛围很适合喜欢拍照的，有布置的鲜花花窗，各种已经摆好的摄影道具，坐椅子上就能拍。</p><h2 id="价格" tabindex="-1">💰价格 <a class="header-anchor" href="#价格" aria-label="Permalink to &quot;:moneybag:价格&quot;">​</a></h2><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>除了蛋糕还有一些小饼干之类的卖，位置在肯德基那一栋二楼，坐电梯上楼就行，她们家还能订生日蛋糕，蛋糕造型做的不错。</p><p><img src="https://s2.loli.net/2024/07/18/t7MnZJjkxP1rQTd.png" alt="image-20240718000707841" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/DnTzNRZH27d5P4E.png" alt="image-20240718000711276" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/ZFIeSxBf87olaci.png" alt="image-20240718000717672" class="mx-auto w-80%"><br></p>',15))])}const P=r(h,[["render",d]]);export{x as __pageData,P as default};
