import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.FYlSEceb.js";import{_ as s,c as o,I as p,j as a,a as r,a3 as l,o as i}from"./chunks/framework.4Gvw3gth.js";const S=JSON.parse('{"title":"南京大排档","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/南京大排档.md","filePath":"Play/Eat/OutSchool/QuanShan/南京大排档.md","lastUpdated":1720104283000}'),n={name:"Play/Eat/OutSchool/QuanShan/南京大排档.md"},c=a("h1",{id:"南京大排档",tabindex:"-1"},[r("南京大排档 "),a("a",{class:"header-anchor",href:"#南京大排档","aria-label":'Permalink to "南京大排档"'},"​")],-1),h=l('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p><img src="https://s2.loli.net/2023/09/22/MCEwUs27GqVL8ph.png" alt="image-20230921220354235" class="mx-auto w-80%"><br></p><p>在苏宁广场三楼</p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>第一次去南京吃到的时候惊为天人，古法糖芋苗和桂花拉糕的味道即便已经过去几年了都还在回味，徐州开了店正好去尝尝。</p><p>桂花拉糕的桂花蜜真的很清澈的甜，是属于适合中国宝宝体质的甜食。这种味道让我觉得用“甜”字来形容都有点俗气了，应该用“甘”。像是在山里徒步了五公里终于找到水源之后喝到的第一口山泉。</p><p>古法糖芋苗里面也放了桂花蜜，应该放了一点藕粉增稠，吃起来像是蜜羹，芋苗应该是芋泥做成的。</p><p>盐水鸭也是经典菜了，可以闭着眼点，但是有时候鸭的皮下脂肪会有一点多，只要不吃多味道还是不错的。</p><p>蜜汁莲藕也可以尝试一下，甜口的，很清爽。</p><p>鸭血旺个人觉得口味有一点过麻了，不太喜欢麻的味道。</p><p>最后就是这个肘子（实在忘记叫什么了，好像叫什么民国什么肘子），脆皮外壳做的实在是太硬了吃着很吃力，而且感觉这个面皮像是春卷皮，感觉用烤鸭面皮或者烙馍会更搭一点，而且如果不蘸甜面酱的话，肘子吃起来完全没有味道，估计是菜单新上的菜，感觉后台还需要调整一下。</p><h2 id="环境" tabindex="-1">🌏环境 <a class="header-anchor" href="#环境" aria-label="Permalink to &quot;:earth_asia:环境&quot;">​</a></h2><p>很惊喜的是徐州的店里居然真的有真人现场的伴唱演奏，虽然坐的位置看不到，不过吃饭的时候一直能听到曲声。</p><h2 id="价格" tabindex="-1">💰价格 <a class="header-anchor" href="#价格" aria-label="Permalink to &quot;:moneybag:价格&quot;">​</a></h2><p>价格感觉很亲民了，在大众点评上有优惠买单。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>周末去人感觉还挺多的，可以提前在大众点评线上排队，过号了等到了现场会帮你往后延三桌。</p><p><img src="https://s2.loli.net/2023/09/21/O6PcMwB2jIQYT3z.png" alt="image-20230921220839384" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/21/fYRmCyDxW6OShkA.png" alt="image-20230921220844715" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/21/CfdZyzlSRmnx32p.png" alt="image-20230921220853565" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/21/BYz8LPVE9hDQWJi.png" alt="image-20230921220902921" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/21/sWPiQ3ZCFROVGbX.png" alt="image-20230921220912056" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/21/AuWpMlafh8Sd1tc.png" alt="image-20230921220919036" class="mx-auto w-80%"><br></p>',23);function m(d,_,u,g,b,f){const t=e;return i(),o("div",null,[c,p(t,{readTime:"2",words:"622"}),h])}const q=s(n,[["render",m]]);export{S as __pageData,q as default};
