import{_ as o}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.YRY2vHzV.js";import{_ as r,c as s,j as t,a as l,G as i,a4 as n,o as p}from"./chunks/framework.stX8LI7J.js";const x=JSON.parse('{"title":"卢氏跑马汤","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/卢氏跑马汤.md","filePath":"Play/Eat/OutSchool/QuanShan/卢氏跑马汤.md","lastUpdated":1721233701000}'),h={name:"Play/Eat/OutSchool/QuanShan/卢氏跑马汤.md"};function c(d,a,m,u,_,b){const e=o;return p(),s("div",null,[a[0]||(a[0]=t("h1",{id:"卢氏跑马汤",tabindex:"-1"},[l("卢氏跑马汤 "),t("a",{class:"header-anchor",href:"#卢氏跑马汤","aria-label":'Permalink to "卢氏跑马汤"'},"​")],-1)),i(e,{readTime:"1",words:"526"}),a[1]||(a[1]=n('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>泉山区北京北路铜山中学东侧（离地铁三号线翟山站2号口近）</p><p><img src="https://s2.loli.net/2024/07/17/5JI6UdcGkMg9bqm.png" alt="image-20240717235725166" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>这次去点了极品烤羊排、卤牛排、芦蒿腊香干和跑马汤。</p><p>【极品烤羊排】：招牌菜，特地为了烤羊排去的。确实做的不错，羊排上面淋了二八酱（花生芝麻酱），手抓起来入口最开始尝到的是二八酱的浓香，然后就是烤的酥脆的外层羊肉，最后是鲜香细软的里层羊肉。调味上也做的正好，酱料和香料不会夺了羊肉的香气，二八酱一开始吃觉得略有浓稠，但是吃到后面的时候发现是点睛之笔。</p><p>【跑马汤】：也是招牌，味道不错，据说大补且养生。汤很浓郁，与其说是汤，不如说是羹，用的是佛跳墙的做法，是徐州的一道名菜，汤是按例卖的，每人一例，乘上来的汤里面有许多薏米做底，薏米并没有炖的很烂，应该不是跟汤一起煨的，除此之外，配汤的炒米是包装的，不是现炒的。</p><p>【卤牛排】：中规中矩，没有什么特色。</p><p>【芦蒿腊香干】：芦蒿和香干搭配起来，口感有脆也有软，芦蒿炒的火候正好清脆解腻。</p><h2 id="环境" tabindex="-1">🌏环境 <a class="header-anchor" href="#环境" aria-label="Permalink to &quot;:earth_asia:环境&quot;">​</a></h2><p>环境还行，有不少包间，中间还有戏台，据说晚上会更热闹些，中间的戏台被流水围着，里面还有鲤鱼，挺有意思。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>点菜方式有点像大张烙馍村，可以看着菜的样品想吃什么自己慢慢选。大众点评上面有代金券。建议3到4个人吃能吃的比较多样。人均在150+左右。</p><p><img src="https://s2.loli.net/2024/07/17/CbfwZGVydrkRp6J.png" alt="image-20240717235755945" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/zCPy6ljFJrI5X4k.png" alt="image-20240717235759031" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/VbKkiWmYhowRL4l.png" alt="image-20240717235802512" class="mx-auto w-80%"><br></p>',16))])}const P=r(h,[["render",c]]);export{x as __pageData,P as default};
