import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.BBI-AS5t.js";import{_ as o,c as s,I as r,j as a,a as p,a6 as i,o as l}from"./chunks/framework.DrBMTUlI.js";const q=JSON.parse('{"title":"四湖鱼村（全鱼宴）","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/四湖鱼村.md","filePath":"Play/Eat/OutSchool/QuanShan/四湖鱼村.md","lastUpdated":1721233701000}'),n={name:"Play/Eat/OutSchool/QuanShan/四湖鱼村.md"},c=a("h1",{id:"四湖鱼村-全鱼宴",tabindex:"-1"},[p("四湖鱼村（全鱼宴） "),a("a",{class:"header-anchor",href:"#四湖鱼村-全鱼宴","aria-label":'Permalink to "四湖鱼村（全鱼宴）"'},"​")],-1),h=i('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>西安北路事业小区1号楼1楼103、104（金庭嘉园对面）</p><p><img src="https://s2.loli.net/2024/07/18/XzsupM8fEVD51dO.png" alt="image-20240718001008781" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了生汆鱼片、蒜香鱼丝、椒盐鱼条三个菜。</p><p>【生汆鱼片】：鱼片片得很薄，但鱼肉新鲜所以薄而不散，筷子可以成片夹起。可以沾辣椒酱或是芥末吃，跟生鱼片比又是不一样的口感。美中不足的是带刺，吃的时候需要小心。</p><p>【蒜香鱼丝】：好吃，鱼肉切成了条状，但是不像猪肉牛肉一样在嘴里的纤维感强，是很细嫩的口感，调味做的也很香，不会过咸或者太辣，鱼肉的味道不会被调味抢掉，或许可以算是真正的“鱼香肉丝”hhh。</p><p>【椒盐鱼条】：炸鱼条蘸酱吃，味道吃起来像松鼠鳜鱼，但是因为是自己蘸酱吃所以不会出现太过酸甜的感觉，虽然是炸的但是不会有很多油，还是有刺，需要注意。炸鱼条的鱼没有前两道菜新鲜。</p><p>其它推荐的就是招牌菜栏目里面的和鱼汤。</p><p>店里没什么饮料可选，喜欢喝饮料的可以考虑自带。</p><h2 id="环境" tabindex="-1">🌏环境 <a class="header-anchor" href="#环境" aria-label="Permalink to &quot;:earth_asia:环境&quot;">​</a></h2><p>徐州普遍饭馆模样</p><h2 id="价格" tabindex="-1">💰价格 <a class="header-anchor" href="#价格" aria-label="Permalink to &quot;:moneybag:价格&quot;">​</a></h2><p>人均：80+左右</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>据说饭点的时候很多人，周末去的话记得提前，徐州这边的饭馆需要关注一下营业时间，基本上是固定饭点才营业，去晚了可能吃不上。</p><p>在大众点评上搜“全鱼宴”可以看到两家店，看了一下定位不是同一家店，去吃饭的这家店的店名叫【四湖鱼村】，注意别弄混了。</p><p><img src="https://s2.loli.net/2024/07/18/yzAVru5i4cavpUm.png" alt="image-20240718001109712" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/Iy4DJNsQYUg3GFe.png" alt="image-20240718001117141" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/7UINgc8wYBnOLdo.png" alt="image-20240718001132558" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/utB4KZeGoFqwyPL.png" alt="image-20240718001139675" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/1qnorRBtFOs57YM.png" alt="image-20240718001145775" class="mx-auto w-80%"><br></p>',22);function d(m,_,u,g,b,f){const t=e;return l(),s("div",null,[c,r(t,{readTime:"1",words:"545"}),h])}const S=o(n,[["render",d]]);export{q as __pageData,S as default};
