import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.Da0_xRX-.js";import{_ as s,c as o,I as r,j as a,a as l,a5 as p,o as i}from"./chunks/framework.BhnfStuL.js";const P=JSON.parse('{"title":"多福临砂锅居","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/多福临砂锅居.md","filePath":"Play/Eat/OutSchool/QuanShan/多福临砂锅居.md","lastUpdated":1720104283000}'),n={name:"Play/Eat/OutSchool/QuanShan/多福临砂锅居.md"},c=a("h1",{id:"多福临砂锅居",tabindex:"-1"},[l("多福临砂锅居 "),a("a",{class:"header-anchor",href:"#多福临砂锅居","aria-label":'Permalink to "多福临砂锅居"'},"​")],-1),h=p('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>只要饭点去的稍晚些就需要在门口排队了，他们的生意真的很好。老店应该是湖北路上的那家店，还有一家二店。</p><p><img src="https://s2.loli.net/2024/04/12/R48czfwhxUYNpDn.png" alt="image-20240412140441326" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>他们家的海米冬瓜汤，我真的能记很久。冬瓜和海米能炖出的汤尝一口就能感觉到很鲜，汤的味道是有层次的，第一层是冬瓜带来的味道，第二层是海米带来的甚至感觉有点像瑶柱的鲜味儿，第三层有回甘，说是“鲜甜”两个字完全没有夸大。中午喝完了之后晚上就又念叨着了。推荐要尝尝，鲜的怀疑是加了味精，但是味精做出来的东西口味层次没有这么丰富。</p><p>羊肉白菜挺有意思的，因为没想到是做的辣口，辣倒是不辣，也不会很麻，辣椒在菜里的感觉像是给羊肉去膻的同时增鲜。不过一端上来的时候还是能闻到一丝羊肉的膻味，但是吃起来的时候完全不会感觉羊肉很膻，肉质肥瘦相间。白菜炖的适中，不是软烂的那一种。</p><p>他们家的砂锅一般都会加香菜，如果不爱吃香菜，记得给厨房备注一下。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>店面比较小，建议饭点的话早一点去，大众点评上可以看到他们家的营业时间只有中午和下午的固定时段营业，其他时间段是不开门的，踩点临关门去的晚了也可能会跟你说打烊了。环境一般，但口味真的不错。属于本地人都常来的餐馆。人均大概是 60 ～ 70 的样子。</p><p><img src="https://s2.loli.net/2024/04/12/RYsqHwCyxfZ6tla.png" alt="image-20240412135123959" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/DXv4zfNr3kKBUqh.png" alt="image-20240412135209311" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/PEcUpBSXDYJglVM.png" alt="image-20240412135220494" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/tceCm4L5uvJ2r7x.png" alt="image-20240412135318541" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/9YqSVOkZEG81Uty.png" alt="image-20240412135330888" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/erQBCSvdEVyh7xK.png" alt="image-20240412135344267" class="mx-auto w-80%"><br></p>',15);function m(_,d,u,g,x,f){const t=e;return i(),o("div",null,[c,r(t,{readTime:"2",words:"571"}),h])}const w=s(n,[["render",m]]);export{P as __pageData,w as default};
