import{_ as s}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.BzqRxcJ5.js";import{_ as o,c as l,j as t,a as r,G as n,a4 as i,o as p}from"./chunks/framework.lM09aArF.js";const b=JSON.parse('{"title":"萝蔓西餐厅","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/萝蔓西餐厅.md","filePath":"Play/Eat/OutSchool/QuanShan/萝蔓西餐厅.md","lastUpdated":1722355667000}'),m={name:"Play/Eat/OutSchool/QuanShan/萝蔓西餐厅.md"};function c(d,a,h,u,g,_){const e=s;return p(),l("div",null,[a[0]||(a[0]=t("h1",{id:"萝蔓西餐厅",tabindex:"-1"},[r("萝蔓西餐厅 "),t("a",{class:"header-anchor",href:"#萝蔓西餐厅","aria-label":'Permalink to "萝蔓西餐厅"'},"​")],-1)),n(e,{readTime:"2",words:"757"}),a[1]||(a[1]=i('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>富国街醒狮小区 1 期 3 单元 102 室</p><p><img src="https://s2.loli.net/2024/07/30/MeKQNVmv89ublog.png" alt="image-20240730235629180" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>临时的一次出门，吃了下午茶之后，在脑海中随机 roll 了几个选项，发现很想吃鹅肝，最终决定去试试他们家，毕竟在我的收藏榜单里躺了很久。</p><p>位置并不难找，在富国街的醒狮小区里，一个大隐隐于市的店。不同于街上的喧嚣，店里的环境干净整洁，从装潢到服务都透露着店主人的审美品味，点餐时问菜品推荐，店员能够介绍每一道菜的优缺点，第一次去的时候点了一份红酒鹅肝。不得不说，红酒酱汁做的很惊艳而且非常醇厚酒精在其中已经挥发了剩下的是带着葡萄果香微酸的口味，就着鹅肝入口的那一瞬间，很有点像广式蘸了酸梅汁的烧鹅味道。鹅肝底下衬的是苹果片，他们的鹅肝做的会比日料店的更厚些。</p><p>虽然只点了一道鹅肝的前菜，但是老板非常慷慨的给打了九折，离店前添加了老板的微信，后面还讨论了一些鹅肝的做法，交流过程中能感到老板对于自家菜品的把控以及思考，遂决定第二次特地探访。</p><p>第二次前去点了一份主厨小羊排和水牛芝士塔塔。点菜时店员有特地说明羊排即使很小心的处理也会不可避免的有膻味，问能不能够接受，菜品上桌之后也会介绍相关的吃法。小羊排配的薯泥很有意思，据说是添加了橙汁，如此便很好的中和掉了羊肉的些许腥膻。羊排火候把握的特别好，整体鲜嫩多汁，调味也非常到位，既增添了肉的香气，又没有喧宾夺主。水牛芝士塔塔整体感觉挺有意思，他们家做的各种酱汁味道都不错。</p><p>除此之外，还送了一份主厨沙拉，虽然说是赠送的菜，但是用料非常实在，沙拉的培根放的非常的满，面包丁没记错的话是烤的干而脆的，整体来说，他们的服务细节真的算是在徐州吃到的最好的一家店。</p><p>味道，菜品什么的都不会出错，建议可以添加店家的微信，因为有些菜需要提前预订，店里的环境也很棒。</p><p><img src="https://s2.loli.net/2024/07/30/4XGuP5OtJznUmRr.jpg" alt="img" class="mx-auto w-80%"><br><img src="https://s2.loli.net/2024/07/30/B7yAwXGFdmgx9cI.jpg" alt="img" class="mx-auto w-80%"><br><img src="https://s2.loli.net/2024/07/30/n9OzBP8FDhslcKk.jpg" alt="img" class="mx-auto w-80%"><br><img src="https://s2.loli.net/2024/07/30/fM4YEBevJIZVQHm.jpg" alt="img" class="mx-auto w-80%"><br><img src="https://s2.loli.net/2024/07/30/JhMmIugO5ALqjyl.jpg" alt="img" class="mx-auto w-80%"><br></p>',11))])}const P=o(m,[["render",c]]);export{b as __pageData,P as default};
