import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CvyqzF0c.js";import{_ as o,c as s,I as r,j as a,a as i,a6 as n,o as l}from"./chunks/framework.D2dq4eyR.js";const S=JSON.parse('{"title":"青辰法式甜品","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/青辰法式甜品.md","filePath":"Play/Eat/OutSchool/QuanShan/青辰法式甜品.md","lastUpdated":1721233701000}'),c={name:"Play/Eat/OutSchool/QuanShan/青辰法式甜品.md"},p=a("h1",{id:"青辰法式甜品",tabindex:"-1"},[i("青辰法式甜品 "),a("a",{class:"header-anchor",href:"#青辰法式甜品","aria-label":'Permalink to "青辰法式甜品"'},"​")],-1),h=n('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>泉山区中枢街95号</p><p><img src="https://s2.loli.net/2024/07/18/5NBCbmaHv8Mf9IS.png" alt="image-20240718000416095" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了香橙蒙布朗蛋糕。店里还有些饮品可选，下次来尝试。</p><p>厚厚的蒙布朗，松软的海绵蛋糕，橙子冻夹心，栗子夹心，还有像芝士蛋糕一样的乳酪，份量对得起价格，橙子冻的加入很惊喜，像个小精灵，缓解了蒙布朗的厚重感觉，很平衡，乳酪在橙子冻底下更衬托出了橙子的清爽的感觉，同时乳酪的浓重又被海绵蛋糕很好的过渡了，没有抢掉蒙布朗和香橙的风味，只突出了绵密顺滑的口感以及独特的乳酪味道，夹心里有栗子碎，口感上会有实感，不会轻飘飘的，栗子的味道不抢戏，跟蛋糕融合的很好，蒙布朗的份量稍微再少那么一点点就完美了。</p><h2 id="环境" tabindex="-1">🌏环境 <a class="header-anchor" href="#环境" aria-label="Permalink to &quot;:earth_asia:环境&quot;">​</a></h2><p>很温暖的小甜品店，有靠窗的位置，虽然窗外没有什么景色。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>想有更多选择的话建议早来，店里生意很好，络绎不绝的，下午三点店里的蛋糕就已经所剩无几了。另外，他们家有生日蛋糕可以定制。</p><p><img src="https://s2.loli.net/2024/07/18/oHEkrfQ1AvuLycJ.png" alt="image-20240718000437546" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/Uxu6mQLiJ7GovNX.png" alt="image-20240718000440746" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/18/UmBt3sr2e8Zya1Q.png" alt="image-20240718000444554" class="mx-auto w-80%"><br></p>',13);function _(d,m,u,f,g,b){const t=e;return l(),s("div",null,[p,r(t,{readTime:"1",words:"393"}),h])}const T=o(c,[["render",_]]);export{S as __pageData,T as default};
