import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.yBKqk1BA.js";import{_ as o,c as s,I as r,j as a,a as n,a6 as i,o as l}from"./chunks/framework.37nz5jb7.js";const S=JSON.parse('{"title":"黑牛无双（夹河街店）","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/QuanShan/黑牛无双.md","filePath":"Play/Eat/OutSchool/QuanShan/黑牛无双.md","lastUpdated":1721233701000}'),p={name:"Play/Eat/OutSchool/QuanShan/黑牛无双.md"},c=a("h1",{id:"黑牛无双-夹河街店",tabindex:"-1"},[n("黑牛无双（夹河街店） "),a("a",{class:"header-anchor",href:"#黑牛无双-夹河街店","aria-label":'Permalink to "黑牛无双（夹河街店）"'},"​")],-1),_=i('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>在夹河街和富国街交界位置</p><p><img src="https://s2.loli.net/2024/07/17/ovtbfBEzMAJ5dIS.png" alt="image-20240717234428447" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了招牌的几个牛肉和鳗鱼。</p><p>【鳗鱼】最推荐点的是他们家的鳗鱼。点单了之后是现杀的，送上桌的时候鳗鱼肉甚至还在跳动。师傅会教你怎么烤以及如何把握火候。烤之前先用配的柠檬洒一遍去去腥，下炉子时注意先烤带鱼皮那面，等鱼皮稍微卷边时换一面，每三四十秒就翻面。鳗鱼没有刺儿，烤出来蘸着烤肉酱吃，是跟牛肉不一样的鲜嫩，一下子就get到了烤鳗鱼的美味。</p><p>【牛肉】肉很新鲜，点的牛肋条烤出来油润喷香，瘦而不柴，肥瘦正正好，牛油一烤香气四溢。牛舌烤出来比较一般。</p><p>【小菜】有韩式泡菜和腌萝卜可以解解腻，烤肉可以选择蘸酱或者蘸调料粉。烤鳗鱼单独配了一碟烧烤酱。还点了一份生菜，生菜挺新鲜的，没有出现蔫了吧唧的叶子。</p><p>但是店里没有什么酒选，如果有嗨棒就锦上添花了。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>大众点评上有套餐，感觉点套餐会更划算些。建议去之前提前打电话，让店家留个位置。店面不算大，饭点的时候需要等位等很久。</p><p><img src="https://s2.loli.net/2024/07/17/1v7I8L92KzUBwAn.png" alt="image-20240717234555125" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/CB8QWZab12lHU4E.png" alt="image-20240717234600262" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/znMAaCsGc1vYt6g.png" alt="image-20240717234604409" class="mx-auto w-80%"><br></p>',14);function h(d,m,u,g,f,b){const t=e;return l(),s("div",null,[c,r(t,{readTime:"1",words:"432"}),_])}const T=o(p,[["render",h]]);export{S as __pageData,T as default};
