import{_ as o}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.BzqRxcJ5.js";import{_ as s,c as r,j as t,a as i,G as l,a4 as p,o as n}from"./chunks/framework.lM09aArF.js";const b=JSON.parse('{"title":"明记肠粉","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/Vicinity/明记肠粉.md","filePath":"Play/Eat/OutSchool/Vicinity/明记肠粉.md","lastUpdated":1720104283000}'),c={name:"Play/Eat/OutSchool/Vicinity/明记肠粉.md"};function m(d,a,h,f,g,u){const e=o;return n(),r("div",null,[a[0]||(a[0]=t("h1",{id:"明记肠粉",tabindex:"-1"},[i("明记肠粉 "),t("a",{class:"header-anchor",href:"#明记肠粉","aria-label":'Permalink to "明记肠粉"'},"​")],-1)),l(e,{readTime:"1",words:"415"}),a[1]||(a[1]=p('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>店面就在小南门，很好找，店里有几张小桌子。</p><p><img src="https://s2.loli.net/2023/09/22/1VPp7J5ajhYEbof.png" alt="image-20230914230515424" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>老板娘人很好，真的有在用心研究菜品，每天也会在群里发点小红包福利。</p><p>最推荐的是肠粉，外卖也能点的到，属于是广东人认证合格的肠粉，肠粉的酱汁也不错，鲜虾肠粉据说是每天早上起大早现选的鲜虾然后剥的。</p><p>分量就是普通肠粉的分量（也就是如果当午饭一份的话可能吃不饱）。</p><p>最近新上了粿条（可以理解为是广东潮汕地区特有的一种米粉），肉放的挺多的，猪肝稍微有一点点韧，不够嫩，粿条的汤喝起来有一种“面”的感觉，不太知道汤具体放了什么，感觉没有骨头汤那种清甜浓郁的鲜香，汤的口味比较偏北方一点，另外就是粿条调味上真的非常非常的淡，连我一个广东人尝起来都觉得有点太淡了，不过桌上有调料瓶可以看口味自己调，最后就是粿条里面的炸蒜总觉得没有起到提香的作用。</p><p>有些时候会有卤味（还没尝，尝了补上）。</p><p><img src="https://s2.loli.net/2023/09/14/HWpLf9BcZRYDsCA.png" alt="image-20230914230614398" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/14/nYy1sgkSWBUPh6f.png" alt="image-20230914230620122" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/14/nYy1sgkSWBUPh6f.png" alt="0" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2023/09/14/JTEBCVfPUzoD54W.png" alt="image-20230914230632343" class="mx-auto w-80%"><br></p>',13))])}const x=s(c,[["render",m]]);export{b as __pageData,x as default};
