import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CdNxDc6j.js";import{_ as o,c as s,I as r,j as a,a as i,a6 as l,o as p}from"./chunks/framework.DrBMTUlI.js";const T=JSON.parse('{"title":"红枣坊","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/Vicinity/红枣坊.md","filePath":"Play/Eat/OutSchool/Vicinity/红枣坊.md","lastUpdated":1721233701000}'),n={name:"Play/Eat/OutSchool/Vicinity/红枣坊.md"},c=a("h1",{id:"红枣坊",tabindex:"-1"},[i("红枣坊 "),a("a",{class:"header-anchor",href:"#红枣坊","aria-label":'Permalink to "红枣坊"'},"​")],-1),_=l('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>矿大小南门主路沿街</p><p><img src="https://s2.loli.net/2024/07/17/MTZ9fdND46RA7Os.png" alt="image-20240717234801151" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>闻起来真的很香，每次路过窗口的时候都可以闻到一股很香的枣糕味儿。</p><p>一号是糯米蛋糕，口感我愿称为蒸蛋糕界的麻薯，糯糯的，有蛋糕的松软，也有麻薯的软糯口感，很奇妙。</p><p>二号是拔丝蛋糕，口感很奇妙，吃起来像肉松，也有肉松的味道，不太确定到底有没有放肉松。</p><p>三号是招牌枣糕，很香，可以吃到碾碎了的红枣，不甜，在窗口买新鲜出炉热乎的最好吃。</p><p>四号是切块枣糕，吃起来跟枣糕一样。</p><p>他们家的泡芙做的是合格的，泡芙发起来了，没有塌下去或者实心，里面是纯奶油，没有经过调味，好像甚至没有放糖，喜欢吃原味泡芙的可以尝尝。</p><h2 id="价格" tabindex="-1">💰价格 <a class="header-anchor" href="#价格" aria-label="Permalink to &quot;:moneybag:价格&quot;">​</a></h2><p>美团上可以点到他们家的外卖，不过一般送过来都凉了，好像没有做保温措施。还是热乎的好吃，不过现在开了暖气也可以放暖气片上热热。</p><p>价格很合算，比学校的糕点铺子便宜多了。</p><p><img src="https://s2.loli.net/2024/07/17/gUjNmb1AKvxCToY.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/u1wSUO2QtEov9Ny.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/aynBCzgiepWho4t.png" alt="img" class="mx-auto w-80%"><br></p>',16);function d(h,m,u,g,f,b){const t=e;return p(),s("div",null,[c,r(t,{readTime:"1",words:"379"}),_])}const y=o(n,[["render",d]]);export{T as __pageData,y as default};
