import{_ as o}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.B8umwdis.js";import{_ as r,c as i,j as t,a as s,G as n,a4 as l,o as d}from"./chunks/framework.CDuSN2nq.js";const x=JSON.parse('{"title":"鸡柳大人-小南门美味炸货铺","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/Vicinity/鸡柳大人.md","filePath":"Play/Eat/OutSchool/Vicinity/鸡柳大人.md","lastUpdated":1721233701000}'),p={name:"Play/Eat/OutSchool/Vicinity/鸡柳大人.md"};function c(h,a,m,_,u,f){const e=o;return d(),i("div",null,[a[0]||(a[0]=t("h1",{id:"鸡柳大人-小南门美味炸货铺",tabindex:"-1"},[s("鸡柳大人-小南门美味炸货铺 "),t("a",{class:"header-anchor",href:"#鸡柳大人-小南门美味炸货铺","aria-label":'Permalink to "鸡柳大人-小南门美味炸货铺"'},"​")],-1)),n(e,{readTime:"1",words:"452"}),a[1]||(a[1]=l('<h2 id="位置" tabindex="-1">🔍 位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag: 位置&quot;">​</a></h2><p>矿大南湖小南门主路沿街往北走100米左右</p><p><img src="https://s2.loli.net/2024/07/18/olnehsfPqd8RXYg.png" alt="image-20240718000050873" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了招牌三合一（鸡柳+薯条+年糕）。</p><p>“很多人钟情于街头巷尾、市井里弄，只有这个环境，才配得上他们想吃出点境界的企图，大家其实很懂生活。” 虽然炸物并不能算得上很健康但偶尔就是会馋这一口啊。 三合一分量很足，而且有大、中、小份可选，铺子看着卫生还算凑合。在铺子买的现炸出来的鸡柳薯条和年糕非常香，年糕是那种薄薄的但是细嚼起来有年糕的韧劲，还带了一点甜；鸡柳条裹的外皮正正好，不会出现一口下去全是油炸外衣没有肉的情况，薯条炸的也好吃，很酥脆。可以根据个人口味选择加辣或者不加辣，口味上有一些偏甜口，不过调味做的刚刚好，不会有调料太多或者味道不足的情况，而且虽然是炸物但是油过滤的很干净，吃起来不会很油腻，甚至可以说吃不到什么油。价格也合适，中份的13元感觉分量不少。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>美团外卖也能点的到他们家外卖，不过外卖送过来虽然有保温袋，但热气在保温袋里一烘薯条会有软掉的风险，除了三合一也可以单点鸡柳或其他两样，个人感觉比小南门很多出摊的水准要高很多，想吃小南门可以考虑多走两步上去吃这家店。</p>',8))])}const g=r(p,[["render",c]]);export{x as __pageData,g as default};
