import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.FYlSEceb.js";import{_ as s,c as o,I as p,j as a,a as r,a3 as l,o as n}from"./chunks/framework.4Gvw3gth.js";const C=JSON.parse('{"title":"Temp°C(彭城大院店)","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/YunLong/Temp度C.md","filePath":"Play/Eat/OutSchool/YunLong/Temp度C.md","lastUpdated":1720104283000}'),i={name:"Play/Eat/OutSchool/YunLong/Temp度C.md"},c=a("h1",{id:"temp°c-彭城大院店",tabindex:"-1"},[r("Temp°C(彭城大院店) "),a("a",{class:"header-anchor",href:"#temp°c-彭城大院店","aria-label":'Permalink to "Temp°C(彭城大院店)"'},"​")],-1),m=l('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>在回龙窝那一片，停车场旁边，高德上的定位不是太准确，在大楼的背面</p><p><img src="https://s2.loli.net/2024/04/12/5zWUdkF14QlorCe.png" alt="image-20240412133427374" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>店面环境不错，挺大的，装修也算用心，小资风格，在徐州感觉还挺难得。</p><p>点了香煎鹅肝和牛肉汉堡，鹅肝配上烤的香脆的吐司，再加上覆盆子的一点果香和酸甜，口感层次很丰富。最惊喜的的点缀的一点豌豆尖，刚入口的时候觉得有点喧宾夺主，但是一口吃下去各种食材混合之后豌豆尖特有的香气就融进了里面，像舌尖上的交响乐。牛肉汉堡也做的很好，芝士被温度融化了覆在牛肉饼上，牛肉饼多汁鲜嫩，一口下去切身体会“大口吃肉”的快乐。</p><p>黄油啤酒也不错。点的是有酒精版的，也有无酒精版。很清爽，一点不厚重，黄油提供了口味上的层次使得啤酒显得更加清爽。但是没有环影的喝起来那么惊艳，不过环影的喝到后面就太甜腻了，他们家的胜在温和清爽上，就算是酒精版感觉对不爱喝酒人士也很友好。</p><p>最后就是那道名字叫“也许是柠檬塔”的甜品。个人觉得味道不太行，创意不错，但是甜品的口感层次没有做好，顶层的蛋白糖是好看的，下面覆盖的是奶油和糖水橘子，最底下似乎是捣碎的黄油饼干。蛋白糖太多了，奶油也太多了，水果的比例少了，奶油直接把水果的清新吞掉了，最底下的饼干碎屑没有起到丰富口感和平衡奶油的作用，因为很难舀起来吃到，总的来说满分五星算的话只能给个三星。</p><p>鸡块小食味道没有什么特别的吸引人的地方，在此不多赘述。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>价格：人均 150+</p><p><img src="https://s2.loli.net/2024/04/12/215nL98vFuwWOrV.png" alt="image-20240412133553163" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/K6iGF8QRJ4g3ZfW.png" alt="image-20240412133605452" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/DHfnJBKNa3Tlbus.png" alt="image-20240412133958149" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/Kr3R8vTD2bhHz79.png" alt="image-20240412133643167" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/GCnX4ahurRk7BwV.png" alt="image-20240412133657631" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/ISBu98Nxrj7vYsl.png" alt="image-20240412133707522" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/12/vrnRd4hqWcV6kDF.png" alt="image-20240412133718470" class="mx-auto w-80%"><br></p>',18);function h(d,_,g,u,T,b){const t=e;return n(),o("div",null,[c,p(t,{readTime:"2",words:"662"}),m])}const P=s(i,[["render",h]]);export{C as __pageData,P as default};
