import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.DBoQP7p-.js";import{_ as s,c as o,I as l,j as a,a as r,a6 as n,o as p}from"./chunks/framework.C5DsrDrp.js";const P=JSON.parse('{"title":"味旬甜品","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/YunLong/味旬甜品.md","filePath":"Play/Eat/OutSchool/YunLong/味旬甜品.md","lastUpdated":1720104283000}'),c={name:"Play/Eat/OutSchool/YunLong/味旬甜品.md"},i=a("h1",{id:"味旬甜品",tabindex:"-1"},[r("味旬甜品 "),a("a",{class:"header-anchor",href:"#味旬甜品","aria-label":'Permalink to "味旬甜品"'},"​")],-1),m=n('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>解放路戏马台西南李家大楼</p><p><img src="https://s2.loli.net/2024/04/08/EJCMPtKDQ8nNwe3.png" alt="image-20240408203337567" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>应该可以说是目前吃过的徐州甜品店环境最好的两家之一，在户部山的李家大院里面。有一个进深的小院子所以隔绝了街道的嘈杂，店里面的窗景也能看到院子里面漂亮的、店主人精心打理的植物。</p><p>坐在窗边的小桌子上打开窗让风和阳光照进来，再点一份甜品配上喜欢的咖啡或者茶，会有一种“当下就是最好”的念头，是一个会让人贪恋的午后。</p><p>甜品点了“柠檬外交官”和甜品柜子里面那个看起来在“冒汗”的粉色桃子。饮品点了抹茶牛奶和西柚果茶。柠檬外交官的状态实在奇妙，像是并没有凝固的奶酪糊糊，上桌的时候估计是模具还带走了一小块然后缺角了，只有两层，是很鲜明的酸，但顶上那一层缓和了“酸”的感觉，带来了一点点回甘，所以整体像是咬到了一口不那么酸的鲜柠檬，即使是糊状也没有带来黏腻的感觉。桃子的那一款巧克力的外壳可以说薄厚做的刚刚好，造型外面的“看起来在冒汗”可能模仿的是桃子刚摘下来鲜嫩欲滴的状态，不过放在甜品柜子里面的时候真的很容易误以为是结了冷凝水。里面有像奶冻一样的口感和果酱夹心，整体的口感也是很轻盈的状态，果酱很清新，甜度的把握很微妙，是令人愉悦的甜，即使对于不爱吃甜的人也很好接受，从造型来看感觉主理人的甜品水平很高。</p><p><img src="https://s2.loli.net/2024/04/08/wWDcFlJiZobdNLr.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/pStxIvdbqs2fT9A.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/RS75WXjf3sVcEwF.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/C2uTZgUJwF7j9PO.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/WEp2J6wkMhVTB3d.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/SmbJjQ7wCxfcBER.png" alt="img" class="mx-auto w-80%"><br></p>',13);function _(d,h,g,u,f,b){const t=e;return p(),o("div",null,[i,l(t,{readTime:"2",words:"592"}),m])}const S=s(c,[["render",_]]);export{P as __pageData,S as default};
