import{_ as o}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.DzE2HbnG.js";import{_ as s,o as p,c as r,j as t,a as l,G as n,a4 as i}from"./chunks/framework.Bd6xQ1Yj.js";const x=JSON.parse('{"title":"Temp°C(彭城大院店)","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/YunLong/莞记.md","filePath":"Play/Eat/OutSchool/YunLong/莞记.md","lastUpdated":1721233701000}'),m={name:"Play/Eat/OutSchool/YunLong/莞记.md"};function c(d,a,h,u,g,b){const e=o;return p(),r("div",null,[a[0]||(a[0]=t("h1",{id:"temp°c-彭城大院店",tabindex:"-1"},[l("Temp°C(彭城大院店) "),t("a",{class:"header-anchor",href:"#temp°c-彭城大院店","aria-label":'Permalink to "Temp°C(彭城大院店)"'},"​")],-1)),n(e,{readTime:"1",words:"452"}),a[1]||(a[1]=i('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>在户部山门口，很好找，大张烙馍村对面。</p><p><img src="https://s2.loli.net/2024/07/17/SvLAWjxEnV8auyb.png" alt="image-20240717233635444" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>新上的港式奶茶个人觉得味道还不错，有港式茶餐厅的风味了，但是是已经提前加过糖了的。不太确定有没有无糖的，如果后面能提供糖包自己选择是否加糖就更好了。</p><p>是味道过得去的早茶，以前吃的时候总觉得他家的虾饺皮一夹就烂了，现在好像好些了，虾饺味道合格。他们家菠萝油的面包不够热，导致黄油吃的时候还比较硬，但是在徐州能吃到菠萝油已经很满足了。没记错的话店里还有榴莲酥，有一次点菠萝油的时候，菠萝油的黄油串了榴莲的味儿，可能是厨房没有分类处理好食材。</p><p>黑椒牛仔骨和金钱肚都是早茶老几样了，之前吃的时候总觉得他们家黑椒味儿太冲了。跟食材结合的不够好，好像最近也有改进了。</p><p>有一个蟹籽的新品（橘黄色皮那份）调味没有做的太好，外面的皮也有一种半生不熟的感觉。</p><p>豉汁凤爪做的不错的，软烂而且入味。红米肠也可以点来尝尝。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>充会员卡好像有优惠，早茶点心大概是 25 左右一份。</p><p><img src="https://s2.loli.net/2024/07/17/zKFCALuiaOI1wUh.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/wusbSvPayXMCm8K.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/wusbSvPayXMCm8K.png" alt="img" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/07/17/bJe1tyVGRjfZNIv.png" alt="img" class="mx-auto w-80%"><br></p>',15))])}const P=s(m,[["render",c]]);export{x as __pageData,P as default};
