import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CSBJqdyJ.js";import{_ as o,c as s,I as p,j as a,a as r,a6 as l,o as n}from"./chunks/framework.DrBMTUlI.js";const S=JSON.parse('{"title":"马市街饣它汤","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/YunLong/马市街饣它汤.md","filePath":"Play/Eat/OutSchool/YunLong/马市街饣它汤.md","lastUpdated":1720104283000}'),c={name:"Play/Eat/OutSchool/YunLong/马市街饣它汤.md"},i=a("h1",{id:"马市街饣它汤",tabindex:"-1"},[r("马市街饣它汤 "),a("a",{class:"header-anchor",href:"#马市街饣它汤","aria-label":'Permalink to "马市街饣它汤"'},"​")],-1),_=l('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p><img src="https://s2.loli.net/2023/09/11/qp3U9gwkA6ajMlZ.png" alt="image-20230911234711676" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>很多徐州本地人都会去吃的一个店，早上人最多，推荐八股油条和他家的 sha 汤（发音同“啥汤”）。</p><p>点单流程是在前台跟柜员点单，然后拿着柜员给你的单子去各个窗口自取你点的餐品。汤可以加一个鸡蛋，付款后柜员会给你一个鸡蛋，你拿着鸡蛋去找做汤的阿姨或大叔让 ta 给你加进汤里就好。油条和锅贴在门外的窗口自取，有时候遇到人多的时候，加上油条基本上是现炸的，一时半会儿会供应不上，这时候需要耐心排队。</p><p>油条可以直接吃也可以配着汤吃，这个就看个人口味。一般一碗汤一根油条就可以吃饱了，吃不饱的话可以考虑加份锅贴或者其他的菜。</p><p>一般人多的时候，一楼店里很难找到座位。建议先找座位，然后再点单。如果不想等位的话，也可以考虑上二楼三楼的雅座。</p><p>二楼和三楼默认是点套餐，如果上二楼三楼的话，一般不需要等座，有座就直接落座，而且菜品会有服务员给你送到桌上。套餐是 38 一位，套餐里面每样菜品都包含一小部分，有油条锅贴水果小菜之类的和两碗汤（没记错的话一碗加了鸡蛋，一碗没加），可以说是北方式的早茶吧。</p><p>综合来说四星推荐。是比较有徐州特色而且味道也不错的一家店。</p><p>这是套餐：</p><p><img src="https://s2.loli.net/2023/09/11/BNUhTLDXqlzaCQk.png" alt="image-20230911234951405" class="mx-auto w-80%"><br></p><p>汤和油条：</p><p><img src="https://s2.loli.net/2023/09/11/fCFWg2hRZBcJy3Q.png" alt="image-20230911235020611" class="mx-auto w-80%"><br></p><p>阿姨在制作：</p><p><img src="https://s2.loli.net/2023/09/22/9X8SuMVLpcfC4kn.png" alt="image-20230911235052874" class="mx-auto w-80%"><br></p>',15);function h(d,m,u,g,f,x){const t=e;return n(),s("div",null,[i,p(t,{readTime:"1",words:"510"}),_])}const T=o(c,[["render",h]]);export{S as __pageData,T as default};
