import{_ as e}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.yBKqk1BA.js";import{_ as o,c as s,I as r,j as a,a as i,a6 as n,o as l}from"./chunks/framework.37nz5jb7.js";const T=JSON.parse('{"title":"鹿巷甜品(户部山店)","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"Play/Eat/OutSchool/YunLong/鹿巷甜品.md","filePath":"Play/Eat/OutSchool/YunLong/鹿巷甜品.md","lastUpdated":1720104283000}'),p={name:"Play/Eat/OutSchool/YunLong/鹿巷甜品.md"},c=a("h1",{id:"鹿巷甜品-户部山店",tabindex:"-1"},[i("鹿巷甜品(户部山店) "),a("a",{class:"header-anchor",href:"#鹿巷甜品-户部山店","aria-label":'Permalink to "鹿巷甜品(户部山店)"'},"​")],-1),_=n('<h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>马市街与彭城路交叉口东 120 米</p><p><img src="https://s2.loli.net/2024/04/08/8HLco1BepnOiq9Z.png" alt="image-20240408212633665" class="mx-auto w-80%"><br></p><h2 id="🌰-食评" tabindex="-1">🌰 食评 <a class="header-anchor" href="#🌰-食评" aria-label="Permalink to &quot;🌰 食评&quot;">​</a></h2><p>点了两个蛋糕。</p><p>【柳橙柑橘风味巴斯克】：整体是做的很好吃的，可以看到橙皮颗粒在底部，增加了橙子风味也缓冲了巴斯克的厚重口感，显得比较轻盈，绵密但又像奶酪般入口即化，后调一点微酸让它的口感没有沉下去，不至于发腻。巴斯克顶上的焦层被奶油盖住了，单独去掉奶油之后还是能吃到焦香的风味，闻起来有很香的芝士和奶油奶酪的味道，口感上多一分就重了，少一分又轻了，把握的很好，整体水准是在的。不过巴斯克这道甜品的口感本身就会比较厚重，再配上顶上没有调味过的不明所以的奶油就有点发腻了，去掉奶油会更好一些。</p><p>【京都柚子】：三层口感，最顶上是水果和鲜奶油，中间是慕斯口感，很绵密，但不会厚重，有一点点果酸的感觉，所以回味很清新。顶层的水果很新鲜，不过柚子的味道不是很鲜明，鲜奶油刚进嘴里立马就融化掉了会有一点点腻，不过很快就被中间的慕斯口感平衡回来了。最底下的海绵蛋糕没有什么存在感，感觉奶油的部分可以稍微做薄一点点。酸是最鲜明的感觉，其它的味道没有特别突出，比吃的另外一个巴斯克更酸一些。</p><h2 id="tips" tabindex="-1">💁‍♂️Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;:tipping_hand_man:Tips&quot;">​</a></h2><p>虽然点评网说十二点才开门但是十一点多去也能买到蛋糕，ta 们家蛋糕打包没有做固定，软一点的蛋糕可能会在盒子里面东倒西歪，送人或者叫闪送的话要注意。</p><p><img src="https://s2.loli.net/2024/04/08/9rUE623AcDCxSji.png" alt="image-20240408213137884" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/GZ8k3UtT5ys1jFW.png" alt="image-20240408213147854" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/eOplIMKFJQvncSw.png" alt="image-20240408213204873" class="mx-auto w-80%"><br></p><p><img src="https://s2.loli.net/2024/04/08/BpVltLigh31UdkJ.png" alt="image-20240408213213091" class="mx-auto w-80%"><br></p>',13);function h(d,m,u,g,f,x){const t=e;return l(),s("div",null,[c,r(t,{readTime:"1",words:"576"}),_])}const S=o(p,[["render",h]]);export{T as __pageData,S as default};
