import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.akAPV9fN.js";import{_ as a,c as n,I as c,j as e,a as p,a6 as u,o as s}from"./chunks/framework.37nz5jb7.js";const z=JSON.parse('{"title":"办事基础———党政办公室","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"StuWork/Base/DZB.md","filePath":"StuWork/Base/DZB.md","lastUpdated":1720687664000}'),h={name:"StuWork/Base/DZB.md"},m=e("h1",{id:"办事基础———党政办公室",tabindex:"-1"},[p("办事基础———党政办公室 "),e("a",{class:"header-anchor",href:"#办事基础———党政办公室","aria-label":'Permalink to "办事基础———党政办公室"'},"​")],-1),o=u('<p>党政办公室，简称“党政办”。</p><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>在我们的日常在校生活中，去院党政办公室，一般是需要盖学院级别的章。校党政办公室一般不用去，不在此讨论。</p><p>印章分为两种：学院党委章（党章）和学院章（院章）。</p><p><strong>院章</strong>：有关行政事务的材料，一般需要盖院章。如：缓考申请表、延迟体测申请表、财务报销等</p><p><strong>党章</strong>：和政治有关的材料，一般需要盖党章。如：政审表等</p><h2 id="办事流程" tabindex="-1">办事流程 <a class="header-anchor" href="#办事流程" aria-label="Permalink to &quot;办事流程&quot;">​</a></h2><p>在盖章时，材料上应取得相关领导或负责人的签名或盖章后再去，具体的办事流程请查看生存手册对应部分，切忌直接拿着材料莽到党政办公室，很有可能会被赶回来补证明材料。</p><p>eg1. 教学相关表格一般应教学副院长签字后，再送去盖章。若在教务办公室审核时教务老师盖了教学副院长的签名章，则可直接拿去盖学院章。</p><p>eg2. 政审表等材料，一般先应由辅导员或学工办主任签字后，再送去盖章</p><h2 id="各院党政办地点及联系方式" tabindex="-1">各院党政办地点及联系方式 <a class="header-anchor" href="#各院党政办地点及联系方式" aria-label="Permalink to &quot;各院党政办地点及联系方式&quot;">​</a></h2><p>矿业工程学院：<a href="https://cese.cumt.edu.cn/xygk/jgsz/xzjg.htm" target="_blank" rel="noreferrer">行政机构-中国矿业大学-矿业工程学院 (cumt.edu.cn)</a></p><p>安全工程学院：<a href="https://safe.cumt.edu.cn/xygk/jgsz.htm" target="_blank" rel="noreferrer">机构设置-安全工程学院 (cumt.edu.cn)</a></p><p>力学与土木工程学院：<a href="https://cace.cumt.edu.cn/xygk/zzjg.htm" target="_blank" rel="noreferrer">组织机构-力学与土木工程学院 (cumt.edu.cn)</a></p><p>机电工程学院：<a href="https://cmee.cumt.edu.cn/info/1006/5040.htm" target="_blank" rel="noreferrer">行政机构设置-机电工程学院 (cumt.edu.cn)</a></p><p>信息与控制工程学院：<a href="https://siee.cumt.edu.cn/info/1007/1004.htm" target="_blank" rel="noreferrer">党政管理机构-信息与控制工程学院 (cumt.edu.cn)</a></p><p>资源与地球科学学院：<a href="https://sres.cumt.edu.cn/info/1058/1928.htm" target="_blank" rel="noreferrer">党政办公室-中国矿业大学资源与地球科学学院 (cumt.edu.cn)</a></p><p>化工学院：<a href="https://scet.cumt.edu.cn/6800/list.htm" target="_blank" rel="noreferrer">机构设置 (cumt.edu.cn)</a></p><p>环境与测绘学院：<a href="https://cesi.cumt.edu.cn/xygk/jgsz.htm" target="_blank" rel="noreferrer">机构设置-中国矿业大学环境与测绘学院 (cumt.edu.cn)</a></p><p>电气工程学院：<a href="https://see.cumt.edu.cn/info/1035/1263.htm" target="_blank" rel="noreferrer">https://see.cumt.edu.cn/info/1035/1263.htm</a></p><p>低碳能源与动力工程学院：<a href="https://slepe.cumt.edu.cn/xygk/jgsz.htm" target="_blank" rel="noreferrer">机构设置-低碳能源与动力工程学院 (cumt.edu.cn)</a></p><p>材料与物理学院：<a href="https://smsp.cumt.edu.cn/93/8b/c22494a562059/page.htm" target="_blank" rel="noreferrer">党政管理部门 (cumt.edu.cn)</a></p><p>数学学院：<a href="https://math.cumt.edu.cn/5645/list.htm" target="_blank" rel="noreferrer">机构设置 (cumt.edu.cn)</a></p><p>计算机科学与技术学院：<a href="https://cs.cumt.edu.cn/xygk/zzjg.htm" target="_blank" rel="noreferrer">组织机构-计算机科学与技术学院 (cumt.edu.cn)</a>（待补）</p><p>经济管理学院：<a href="https://sm.cumt.edu.cn/info/1007/4935.htm" target="_blank" rel="noreferrer">党政管理部门-中国矿业大学经济管理学院 (cumt.edu.cn)</a></p><p>公共管理学院：<a href="https://cllp.cumt.edu.cn/xygk/jgsz.htm" target="_blank" rel="noreferrer">机构设置-中国矿业大学公共管理学院 (cumt.edu.cn)</a></p><p>马克思主义学院：<a href="https://mks.cumt.edu.cn/xygk/jgsz.htm" target="_blank" rel="noreferrer">机构设置-马克思主义学院 (cumt.edu.cn)</a></p><p>外国语言文化学院：<a href="https://sfs.cumt.edu.cn/645/list.htm" target="_blank" rel="noreferrer">机构设置 (cumt.edu.cn)</a></p><p>建筑与设计学院：<a href="https://art.cumt.edu.cn/2589/list.htm" target="_blank" rel="noreferrer">系所机构 (cumt.edu.cn)</a></p><p>人文与艺术学院：<a href="https://rwxy.cumt.edu.cn/xygk/jgsz.htm" target="_blank" rel="noreferrer">机构设置-人文与艺术学院 (cumt.edu.cn)</a></p><p>体育学院：<a href="https://sport.cumt.edu.cn/bd/f7/c10320a638455/page.htm" target="_blank" rel="noreferrer">行政机构设置 (cumt.edu.cn)</a></p><p>孙越崎学院：<a href="https://syq.cumt.edu.cn/8616/list.htm" target="_blank" rel="noreferrer">机构设置 (cumt.edu.cn)</a></p>',32);function l(d,f,_,g,i,k){const t=r;return s(),n("div",null,[m,c(t,{readTime:"2",words:"804"}),o])}const j=a(h,[["render",l]]);export{z as __pageData,j as default};
