import{_ as a}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.B_ghoC2Z.js";import{_ as t,c as i,j as l,a as r,G as s,a4 as n,o as d}from"./chunks/framework.C83m97Xz.js";const k=JSON.parse('{"title":"综合素质测评","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"StuWork/ComprehensiveAssessment/index.md","filePath":"StuWork/ComprehensiveAssessment/index.md","lastUpdated":1733154524000}'),p={name:"StuWork/ComprehensiveAssessment/index.md"};function c(u,e,m,h,g,f){const o=a;return d(),i("div",null,[e[0]||(e[0]=l("h1",{id:"综合素质测评",tabindex:"-1"},[r("综合素质测评 "),l("a",{class:"header-anchor",href:"#综合素质测评","aria-label":'Permalink to "综合素质测评"'},"​")],-1)),s(o,{readTime:"1",words:"502"}),e[1]||(e[1]=n('<blockquote><p>自2022级开始，我校本科生进行综合素质测评的依据文件为《中国矿业大学学生综合素质测评条例（试行）》，并由各学院制定本学院详细实施的条例，供本学院学生执行。</p></blockquote><p>综合素质测评，是学校掌握学生德智体美劳以及全面发展的重要手段，是评奖评优、保研等的重要依据。</p><h2 id="参加人员时间" tabindex="-1">参加人员时间 <a class="header-anchor" href="#参加人员时间" aria-label="Permalink to &quot;参加人员时间&quot;">​</a></h2><p>我校所有全日制在籍本科生，在每年的9月，进行综合素质测评。</p><p>中途转专业的，测评在原班级进行。</p><h2 id="成绩组成" tabindex="-1">成绩组成 <a class="header-anchor" href="#成绩组成" aria-label="Permalink to &quot;成绩组成&quot;">​</a></h2><p>综合素质得分（Z）= 基础素质得分（J）×60% + 发展素质得分（F）× 40%。</p><h2 id="测评等级" tabindex="-1">测评等级 <a class="header-anchor" href="#测评等级" aria-label="Permalink to &quot;测评等级&quot;">​</a></h2><p>分优秀、良好、合格、不合格四个等级。</p><ul><li><strong>优秀</strong>：综合素质得分（Z）位于专业前 20%，且同时符合以下条件。</li></ul><ol><li>基础素质得分（J）排名位于专业前 20%。</li><li>德育基础素质得分≥85 分。</li><li>智育基础素质得分位于专业前 20%。</li><li>体育、美育、劳育基础素质测评得分≥70 分。</li></ol><ul><li><strong>良好</strong>：综合素质得分（Z）位于专业前 60%，且同时符合以下条件。</li></ul><ol><li>基础素质得分（J）排名位于专业前 60%。</li><li><strong>德育</strong>基础素质得分≥75 分。</li><li><strong>智育</strong>基础素质得分位于专业前 60%。</li><li>体育、美育、劳育<strong>基础素质测评</strong>均为合格及以上。</li></ol><ul><li><strong>合格</strong>：须同时符合以下条件。</li></ul><ol><li>德育基础素质测评合格。</li><li>基础素质得分（J）≥60 分。</li><li>智育、体育、美育、劳育基础素质测评有 3 个及以上合格。</li></ol><ul><li><strong>不合格</strong>：学生存在以下情况之一，综合素质测评不合格。</li></ul><ol><li>德育基础素质测评不合格。</li><li>基础素质得分(J)＜60 分。</li><li>智育、体育、美育、劳育基础素质测评有 2 个及以上不合格。</li></ol>',17))])}const b=t(p,[["render",c]]);export{k as __pageData,b as default};
