import{_ as t}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CvyqzF0c.js";import{_ as s,c as l,I as o,j as a,a as i,a6 as r,o as n}from"./chunks/framework.D2dq4eyR.js";const P=JSON.parse('{"title":"课程节点替代","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"Study/CreditRecognition/CourseNodeReplacement.md","filePath":"Study/CreditRecognition/CourseNodeReplacement.md","lastUpdated":1720798161000}'),c={name:"Study/CreditRecognition/CourseNodeReplacement.md"},d=a("h1",{id:"课程节点替代",tabindex:"-1"},[i("课程节点替代 "),a("a",{class:"header-anchor",href:"#课程节点替代","aria-label":'Permalink to "课程节点替代"'},"​")],-1),m=r('<h2 id="概念" tabindex="-1">概念 <a class="header-anchor" href="#概念" aria-label="Permalink to &quot;概念&quot;">​</a></h2><p>课程节点替代，是指学生将自己所修读的一门课程的性质，认证为另一种性质。进行课程节点替代，不会影响到本人的 GPA。</p><h2 id="产生课程节点替代的原因" tabindex="-1">产生课程节点替代的原因 <a class="header-anchor" href="#产生课程节点替代的原因" aria-label="Permalink to &quot;产生课程节点替代的原因&quot;">​</a></h2><ul><li><p>由于转专业的原因，自身已修课程不在新专业的培养方案中，但经过与教务秘书沟通，可以认证为自己专业选修课/跨专业选修课/通识选修课的学分。</p><details class="details custom-block"><summary>Example</summary><p>张三由应用化学专业转专业到了机器人工程专业，其大一下学期所修的“<strong>专业导论</strong>”不在机器人工程专业培养方案中，但经过与教务秘书沟通，可以认证为本人培养方案中<strong>跨专业选修课</strong>的学分。</p></details></li><li><p>由于新培养方案的推出，有专业选修课不再开设而自身所修学分不足，新培养方案中专业主干或大类基础课程安排与旧培养方案不同，且没有课程与已修课程相似，则可以选修新培养方案中的专业主干/专业大类基础/专业选修课，再将课程性质替代为专业选修课。</p><details class="details custom-block"><summary>Example</summary><p>张三是应用化学学生，由于 16 版培养方案中专业选修课”高分子化学与物理“未通过，而 20 版培养方案较 16 版多出了一门专业主干课”实验室安全与技术“，则张三可以选修该课程，并将课程节点替代为”专业选修课“，以满足毕业要求。</p></details></li></ul><h2 id="课程节点替代例子" tabindex="-1">课程节点替代例子 <a class="header-anchor" href="#课程节点替代例子" aria-label="Permalink to &quot;课程节点替代例子&quot;">​</a></h2><details class="details custom-block"><summary>Example</summary><ul><li>2022 级机器人工程转计算机科学与技术的学生，<strong>大学化学 A</strong>可认证成<strong>跨专业选修课</strong>。</li></ul></details><h2 id="操作流程" tabindex="-1">操作流程 <a class="header-anchor" href="#操作流程" aria-label="Permalink to &quot;操作流程&quot;">​</a></h2><ul><li>教务系统-报名申请-校内课程节点替代申请（定制）-申请</li></ul><img src="https://s2.loli.net/2024/07/12/jFGRpHEgK6dkevZ.png" alt="image-20240712143009696" class="mx-auto w-70%"><ul><li><p>选择校内课程-选择你要进行节点替代的那门课</p></li><li><p>选择要替代为的节点</p></li><li><p>备注里填写替代理由</p></li><li><p>确定，等待学院教务审核，通过后即替代成功</p><img src="https://s2.loli.net/2024/07/12/LOsxvjpeQYquhcb.png" alt="image-20240712142830411" class="mx-auto w-70%"></li></ul>',10);function p(u,h,_,g,f,x){const e=t;return n(),l("div",null,[d,o(e,{readTime:"1",words:"575"}),m])}const N=s(c,[["render",p]]);export{P as __pageData,N as default};
