import{_ as a}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.-EIUnVAE.js";import{_ as r,c as n,j as e,a as i,G as s,a4 as d,o as p}from"./chunks/framework.CDuSN2nq.js";const x=JSON.parse('{"title":"学分认定","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"Study/CreditRecognition/CreditRecognition.md","filePath":"Study/CreditRecognition/CreditRecognition.md","lastUpdated":1725600159000}'),c={name:"Study/CreditRecognition/CreditRecognition.md"};function l(g,t,h,m,f,u){const o=a;return p(),n("div",null,[t[0]||(t[0]=e("h1",{id:"学分认定",tabindex:"-1"},[i("学分认定 "),e("a",{class:"header-anchor",href:"#学分认定","aria-label":'Permalink to "学分认定"'},"​")],-1)),s(o,{readTime:"1",words:"592"}),t[1]||(t[1]=d('<p>本篇的指导文件为<a href="https://cumtlive.lanzout.com/i0x3P10pgw7c" target="_blank" rel="noreferrer">中矿大教字〔2022〕16号《关于印发&lt;中国矿业大学本科学生学分认定办法&gt;的通知》</a>。</p><h2 id="什么是学分认定" tabindex="-1">什么是学分认定 <a class="header-anchor" href="#什么是学分认定" aria-label="Permalink to &quot;什么是学分认定&quot;">​</a></h2><p>学分认定是指学生在本科学习期间， 所取得的<strong>非本人所在专业培养计划内的课程学分(或学习成果)</strong>，由教学管理机构认定为其所在专业培养计划内所需课程学分的过程。学分认定的范围包括：学生在校内所取得的非本人所在专业培养计划内的课程学分；学生在国内外其他高校或教育机构修读课程获得的学分；学生参加大学生创新创业训练计划或其他创新创业实践活动等取得的非学历学习成果。（本篇中我们只讨论<strong>第一条</strong>）</p><p>在本篇中，主要介绍因<strong>学籍异动</strong>转专业、降级、提前毕业、休学等）而产生的学分认定需求。</p><p>我们要明白进行学分认定的目的：<strong>满足本人专业培养方案的要求，顺利毕业</strong>。</p><p>进行学分认定的主要方式有两种：<strong>课程替代</strong>和<strong>课程节点替代</strong>。</p><p>我校目前学分认定的负责部门为：<strong>各学院教务办</strong>！！在进行学分认定的工作前，请务必咨询对应学院的本科教学管理办公室的教务秘书，因为各学院政策可能不同，<strong>务必咨询！</strong></p><h2 id="负责单位" tabindex="-1">负责单位 <a class="header-anchor" href="#负责单位" aria-label="Permalink to &quot;负责单位&quot;">​</a></h2><p>目前，学分认定的工作由各学院教务办负责。</p><p>同时，各学院可能会在学校学分认定办法的基础上，增添一些自己学院的规定。</p><h2 id="学分认定如何撤销" tabindex="-1">学分认定如何撤销 <a class="header-anchor" href="#学分认定如何撤销" aria-label="Permalink to &quot;学分认定如何撤销&quot;">​</a></h2><p>按照指导文件的规定，学分认定流程系统上一旦通过，<strong>原则上</strong>不得撤销。需要撤销的，请写申请书，内容包括提交学分认定的课程，提交时间，审核人，通过时间，申请撤销的理由（详细说明）。然后本人在申请书上签字，带着申请书去本学院教学管理办公室审核，再去找学院的教学副院长签字，最后交到行健楼A102（教务部教学研究与改革中心）。</p>',12))])}const P=r(c,[["render",l]]);export{x as __pageData,P as default};
