import{_ as l}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.yBKqk1BA.js";import{_ as s,c as r,I as n,j as t,a as i,a6 as g,o as e}from"./chunks/framework.37nz5jb7.js";const A=JSON.parse('{"title":"语言与标化考试","description":"","frontmatter":{"Author":"橙子"},"headers":[],"relativePath":"Study/StudyAbroad/Tests.md","filePath":"Study/StudyAbroad/Tests.md","lastUpdated":1722517243000}'),a={name:"Study/StudyAbroad/Tests.md"},d=t("h1",{id:"语言与标化考试",tabindex:"-1"},[i("语言与标化考试 "),t("a",{class:"header-anchor",href:"#语言与标化考试","aria-label":'Permalink to "语言与标化考试"'},"​")],-1),p=g('<h2 id="主流英语语言考试" tabindex="-1">主流英语语言考试 <a class="header-anchor" href="#主流英语语言考试" aria-label="Permalink to &quot;主流英语语言考试&quot;">​</a></h2><ol><li><p><strong>托福 (TOEFL)</strong></p><ul><li><strong>全称</strong>：Test of English as a Foreign Language</li><li><strong>用途和特点</strong>：用于申请英语为主要教学语言的大学和学院，特别是美国的学校。听力很困难也很刺激，而且贯穿各个部分</li><li><strong>测试内容</strong>：包括阅读、听力、口语和写作四个部分。</li><li><strong>评分</strong>：每部分 0-30 分，总分 0-120 分。</li><li><strong>技巧</strong>：（笔者没考过，待补充。）</li></ul></li><li><p><strong>雅思 (IELTS)</strong></p><ul><li><strong>全称</strong>：International English Language Testing System</li><li><strong>用途和特点</strong>：用于申请英语为主要教学语言的大学和学院，特别是英联邦国家的学校，如英国、澳大利亚和加拿大。雅思听力相对托福简单，但口语相当困难。<strong>口语和写作在中国大陆地区存在未被官方承认但被考生广泛反应的压分情况</strong>。</li><li><strong>评分</strong>：包括听力、阅读、写作和口语四个部分，每项满分 9 分。</li><li><strong>分数计算</strong>：总分为四项小分的平均分，每满 0.25 向上进（如 7.25 是 7.5 分，7.75 是 8 分，但 7.625 是 7.5 分）</li><li><strong>技巧</strong>：听阅非常应试，多练（ieltscat.xdf.cn 上有免费的剑雅练习题）。写作跟着顾家北熟练模板，整理记忆常用的写作词组（留学群里也传了一份）。口语有题库，每 3 个月更新一次，可以事先准备，然后和 GPT 移动端激情开麦。</li></ul></li><li><p><strong>PTE 学术英语考试 (PTE Academic)</strong></p><ul><li><strong>用途</strong>：用于申请英语为主要教学语言的大学和学院。</li><li><strong>测试内容</strong>：包括阅读、听力、口语和写作四个部分。</li><li><strong>分数范围</strong>：10-90 分。</li></ul></li><li><p><strong>多邻国英语测试 (Duolingo English Test, DET)</strong> <strong>&lt;--隆重介绍</strong></p><ul><li><p><strong>用途</strong>：一般用于申请美国院校，但适用范围不如托福和雅思广。近些年多邻国的推广有疫情的推动，故不保证其在未来仍然受认可。无论如何，多邻国比起其他几种考试相对简单，而且<strong>词汇为王</strong>，最适合速通。</p></li><li><p><strong>测试内容</strong>：</p><ul><li><strong>综合性测试</strong>：包含一系列题目，测试考生的听、说、读、写能力，可能包括听写单词、描述图片、回答口语问题、写短文等题型。</li><li><strong>适应性测试</strong>：问题难度会根据考生的回答情况自动调整，以确保测试结果能够准确反映考生的真实英语水平。（前面做得越好题目越难）</li></ul></li><li><p><strong>测试时长</strong>：测试时间约为 60 分钟，其中包括约 45 分钟的考试部分和 10 分钟的视频面试部分。视频面试中，考生需回答两个开放性问题，用于评估口语表达能力。</p></li><li><p><strong>分数范围</strong>：总分为 10-160 分，分数区间划分为 10 分为一个等级。测试结果通常会在 48 小时内公布，并且包括详细的分数报告。</p></li><li><p><strong>考试形式</strong>：考生可以在任何有稳定互联网连接的地方进行测试。考试前需要进行设备和环境检查，以确保测试的公平性和安全性。</p></li><li><p><strong>优势</strong>：</p><ul><li><strong>便捷性</strong>：考生无需前往指定考点，可以在家中进行考试。（这也可能导致 DET 无端不给你成绩认证）</li><li><strong>性价比高</strong>：相较于其他英语语言考试，费用较低（50 美元左右）。</li><li><strong>快速出分</strong>：通常在 48 小时内可以收到考试结果，并可直接发送给申请的学校。</li><li><strong>灵活性</strong>：考试时间和地点高度灵活（自己电脑上考）。</li></ul></li></ul></li></ol><h2 id="主流标化考试" tabindex="-1">主流标化考试 <a class="header-anchor" href="#主流标化考试" aria-label="Permalink to &quot;主流标化考试&quot;">​</a></h2><p>标化考试，即”标准化考试“，用于标准化评估学生水平。用于硕士申请的一般有 GRE 考试和 GMAT 考试。（其他标准化考试还有 SAT、A-Level、IB 和高考等，这些用于大学本科录取）</p><ol><li><p><strong>GRE 考试</strong></p><ul><li><strong>全称</strong>：Graduate Record Examinations General Test</li><li><strong>用途</strong>：用于申请研究生和商学院课程，特别是在美国。</li><li><strong>测试内容</strong>：包括分析性写作、语言推理和数量推理三个部分。</li><li><strong>分数范围</strong>： <ul><li>语言推理：130-170 分（1 分增量）</li><li>数量推理：130-170 分（1 分增量）</li><li>分析性写作：0-6 分（0.5 分增量）</li></ul></li><li><strong>没到 320 不建议提交！！！</strong></li></ul></li><li><p><strong>GMAT 考试</strong></p><ul><li>主要用于商科申请，类似于 GRE，但笔者不了解，待补充。</li></ul></li></ol>',5);function u(c,_,T,h,m,f){const o=l;return e(),r("div",null,[d,n(o,{readTime:"3",words:"1.1k"}),p])}const P=s(a,[["render",u]]);export{A as __pageData,P as default};
