import{_ as t}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.yBKqk1BA.js";import{_ as o,c as r,I as i,j as a,a as l,a6 as d,o as s}from"./chunks/framework.37nz5jb7.js";const y=JSON.parse('{"title":"为什么要出国","description":"","frontmatter":{"author":"橙子"},"headers":[],"relativePath":"Study/StudyAbroad/WhyGoAbroad.md","filePath":"Study/StudyAbroad/WhyGoAbroad.md","lastUpdated":1722350758000}'),c={name:"Study/StudyAbroad/WhyGoAbroad.md"},n=a("h1",{id:"为什么要出国",tabindex:"-1"},[l("为什么要出国 "),a("a",{class:"header-anchor",href:"#为什么要出国","aria-label":'Permalink to "为什么要出国"'},"​")],-1),_=d('<p>    既然你点进了这篇文章，你对留学肯定是有一定兴趣的。出国是为了什么？想获得更高的学历？享受国外高质量的教学和科研氛围？想体验一下异国生活？现在就读于中外合办专业？<strong>想改变自己考研甚至高考失败的悲剧？</strong>（都没关系，虽然出国大部分价格不菲，但只要你想明白了，Just go ahead！此外，宣传下矿大留学讨论群：558327424。</p><h2 id="对一些误解的解释" tabindex="-1">对一些误解的解释 <a class="header-anchor" href="#对一些误解的解释" aria-label="Permalink to &quot;对一些误解的解释&quot;">​</a></h2><ul><li><p>Q: 出国的都是成绩不好的人？</p></li><li><p>A: 诚然，现在英联邦的很多学校开设了”水硕“，主打一个低门槛短学制，吸引了很多人就读。但这些都是所谓的”cash cow 项目“（中文可能叫做创收项目）。国外仍然有很多高质量的研究性硕士甚至博士，非常值得就读。</p></li><li><p>Q: 出国都要花很多钱？</p></li><li><p>A: 香港和美国的研究性硕士和博士大多都提供全奖，做 RA/TA 还可以获得工资，足够 cover 生活费。此外，非主流留学目的地，如欧陆，很多学校只需要交很少的学费甚至只需要承担生活费。</p></li></ul><h2 id="需要准备什么" tabindex="-1">需要准备什么 <a class="header-anchor" href="#需要准备什么" aria-label="Permalink to &quot;需要准备什么&quot;">​</a></h2><ul><li>一个不错的 GPA（由于矿大的绩点制度出国不占优，建议使用百分制加权）</li><li>竞赛或科研成果（越多越好）</li><li>GRE 成绩/GMAT 成绩（仅针对硕士申请，商科选择 GMAT，其他选择 GRE）</li><li>语言成绩</li><li>推荐信（可能非必须）</li><li>文书</li></ul>',5);function h(p,u,m,A,f,b){const e=t;return s(),r("div",null,[n,i(e,{readTime:"1",words:"410"}),_])}const G=o(c,[["render",h]]);export{y as __pageData,G as default};
