import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.CdNxDc6j.js";import{_ as l,c as a,I as i,j as t,a as e,a6 as o,o as s}from"./chunks/framework.DrBMTUlI.js";const S=JSON.parse('{"title":"学费","description":"","frontmatter":{"author":"Yolo3"},"headers":[],"relativePath":"Study/Tuition.md","filePath":"Study/Tuition.md","lastUpdated":null}'),c={name:"Study/Tuition.md"},d=t("h1",{id:"学费",tabindex:"-1"},[e("学费 "),t("a",{class:"header-anchor",href:"#学费","aria-label":'Permalink to "学费"'},"​")],-1),h=o('<p>本文的指导文件为：<a href="https://cwb.cumt.edu.cn/75/81/c18810a619905/page.htm" target="_blank" rel="noreferrer">关于印发《中国矿业大学本科生学分制收费管理办法（修订）》的通知</a></p><h2 id="学费制度" tabindex="-1">学费制度 <a class="header-anchor" href="#学费制度" aria-label="Permalink to &quot;学费制度&quot;">​</a></h2><p>我校目前采用的是<strong>学分制</strong>的学籍管理制度，以学生修读课程的<strong>学分数</strong>作为衡量和计算学生学习量的基本单位，以取得学生所在年级专业培养计划规定的<strong>最低毕业总学分</strong>作为学生毕业的主要标准。</p><h2 id="学分制收费" tabindex="-1">学分制收费 <a class="header-anchor" href="#学分制收费" aria-label="Permalink to &quot;学分制收费&quot;">​</a></h2><p>学分制收费是按学生修读的<strong>学分数</strong>计收学费的制度。</p><p>学分制收费由<strong>专业学费</strong>和<strong>学分学费</strong>两部分组成。目前，我校学分收费标准为<strong>120元/学分</strong>。</p><h2 id="学年学费-2023级为例" tabindex="-1">学年学费（2023级为例） <a class="header-anchor" href="#学年学费-2023级为例" aria-label="Permalink to &quot;学年学费（2023级为例）&quot;">​</a></h2><img src="https://s2.loli.net/2024/08/25/CRUcjGA4pHuv87w.jpg" alt="0ea6f9420044185b0e0378985d60e210" class="mx-auto w-70%"><p>学年学费，是由江苏省物价部门核定的，可以理解为，将学生顺利毕业的最低总成本平摊到每一年，所计算出的一个数字。</p>',9),p={class:"MathJax",jax:"SVG",style:{direction:"ltr",position:"relative"}},g={style:{overflow:"visible","min-height":"1px","min-width":"1px","vertical-align":"0.02ex"},xmlns:"http://www.w3.org/2000/svg",width:"1.76ex",height:"1.09ex",role:"img",focusable:"false",viewBox:"0 -491 778 482","aria-hidden":"true"},_=t("g",{stroke:"currentColor",fill:"currentColor","stroke-width":"0",transform:"scale(1,-1)"},[t("g",{"data-mml-node":"math"},[t("g",{"data-mml-node":"mo"},[t("path",{"data-c":"D7",d:"M630 29Q630 9 609 9Q604 9 587 25T493 118L389 222L284 117Q178 13 175 11Q171 9 168 9Q160 9 154 15T147 29Q147 36 161 51T255 146L359 250L255 354Q174 435 161 449T147 471Q147 480 153 485T168 490Q173 490 175 489Q178 487 284 383L389 278L493 382Q570 459 587 475T609 491Q630 491 630 471Q630 464 620 453T522 355L418 250L522 145Q606 61 618 48T630 29Z",style:{"stroke-width":"3"}})])])],-1),m=[_],u=t("mjx-assistive-mml",{unselectable:"on",display:"inline",style:{top:"0px",left:"0px",clip:"rect(1px, 1px, 1px, 1px)","-webkit-touch-callout":"none","-webkit-user-select":"none","-khtml-user-select":"none","-moz-user-select":"none","-ms-user-select":"none","user-select":"none",position:"absolute",padding:"1px 0px 0px 0px",border:"0px",display:"block",width:"auto",overflow:"hidden"}},[t("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[t("mo",null,"×")])],-1),x=o('<h2 id="学费计算" tabindex="-1">学费计算 <a class="header-anchor" href="#学费计算" aria-label="Permalink to &quot;学费计算&quot;">​</a></h2><ul><li>第二课堂学分免收学分学费</li></ul><img src="https://s2.loli.net/2024/08/25/3PwT5YmjJCnoMrG.png" alt="w-100" class="mx-auto w-100%"><img src="https://s2.loli.net/2024/08/25/Fk4lNtGb5eDMpsg.png" alt="w-100" class="mx-auto w-100%"><img src="https://s2.loli.net/2024/08/25/ESZkR9lTm5Xpnah.png" alt="w-100" class="mx-auto w-100%"><div class="info custom-block"><p class="custom-block-title">如何理解该项收费制度</p><p>从“学年学费”一节的图中可以看出，同一类别的专业，学年学费基本相同（部分专业除外）。这代表着这些专业的培养成本是相同的。但各个专业的培养方案中对<strong>最低毕业总学分</strong>的要求是不同的，为了平衡这种差距，引入了“专业学费”这个说法。</p><p>举个例子：</p><table tabindex="0"><thead><tr><th style="text-align:center;">专业名</th><th style="text-align:center;">数学与应用数学</th><th style="text-align:center;">应用化学</th></tr></thead><tbody><tr><td style="text-align:center;">类别</td><td style="text-align:center;">理科类</td><td style="text-align:center;">理科类</td></tr><tr><td style="text-align:center;">最低毕业学分</td><td style="text-align:center;">155+4+2=161</td><td style="text-align:center;">165+4+4=173</td></tr><tr><td style="text-align:center;">最低毕业学分(除第二课堂)</td><td style="text-align:center;">161-4=157</td><td style="text-align:center;">173-4=169</td></tr></tbody></table><p><strong>数学与应用数学</strong>与</p><p><strong>数学与应用数学</strong>专业2020级培养方案中最低毕业学分为（155+4+2），<strong>应用化学</strong>专业2020级培养方案中最低毕业学分为（165+4+4），扣除掉第二课堂的学分，数学专业：157学分，计算出的学年专业学分</p></div>',6);function b(w,T,f,y,k,v){const n=r;return s(),a("div",null,[d,i(n,{readTime:"2",words:"601"}),h,t("p",null,[e("学年学费"),t("mjx-container",p,[(s(),a("svg",g,m)),u]),e("基本学制年限，即为学生顺利毕业的成本。")]),x])}const L=l(c,[["render",b]]);export{S as __pageData,L as default};
